[21]={scene_type=21,name="一战到底",show_fbicon="btn_outfb#btn_lucky#btn_fbdesc",fb_desc="1、活动开启后进入战场全民PK\n\n2、战场内最多可复活8次\n\n3、活动结束时根据排名给与奖励，击杀数越高，荣誉奖励越多\n\n4、战场内每两分钟随机抽取5人发放幸运奖励\n\n5、屡败屡战，坚持到底即可获赠礼包奖励，奖励通过邮件发放",pb_chenhao=1,pb_wing=1,pb_guanghuan=1,attack_player=1,},
[22]={scene_type=22,name="多人塔防",show_fbicon="btn_outfb#btn_fbdesc#btn_cheerup",fb_desc="1.保护副本中央的新仙界核心不被破坏\n\n2.一共有10波怪，每波怪刷新时间相隔1分钟\n\n3.成功守护新仙界核心10波，即可成功通关\n\n4.新仙界核心被破坏或超过副本时间，则副本失败\n\n5.使用鼓舞可以大幅度提高战力\n\n",pb_chenhao=1,pb_wing=1,pb_guanghuan=1,smallmap_open=1,},
[23]={scene_type=23,name="公会试炼",show_fbicon="btn_outfb#btn_fbdesc#btn_fbxzmy",fb_desc="1.公会试炼由<color=#25a3f5>会长</color>或<color=#25a3f5>副会长</color>开启，活动限时<color=#25a3f5>15</color>分钟\n\n2.活动开启后，玩家进入试炼场景护送<color=#25a3f5>公会守卫</color>，击杀阻挠的怪物\n\n3.场景内怪物击杀完毕，则挑战成功，10秒后进入下一波，守卫血量不会回复",is_auto_guaji=1,},
[24]={scene_type=24,name="五行打宝",show_fbicon="btn_outfb#btn_lucky#btn_fbdesc#btn_wuxingshenjiang",fb_desc="1、场景内可获得五行熔炼材料\n\n2、完成目标任务可获得目标奖励\n\n3、活动场景内每隔4分钟随机送出幸运礼包\n\n4、击杀五行神将可获得大量奖励",pb_wing=1,},
