[23450]={id=23450,name="先轸",pile_limit=200,use_type=79,param1=10,description="  羞小耻以构大怨，贪小利以亡大众；春秋有其戒，晋先轸是也。",icon_id=23450,click_use=0,open_panel="FamousGeneralView#ming_jiang",drop_icon=23450,is_tip_use=0,choose_use=0,power=9000,is_display_role=32,special_show=1,rarefloating=1,},
[23451]={id=23451,name="吴起",pile_limit=200,use_type=79,param1=11,description="  吴起，名将也。在德不在险之一言，亦似闻道者欤？",icon_id=23451,click_use=0,open_panel="FamousGeneralView#ming_jiang",drop_icon=23451,is_tip_use=0,choose_use=0,power=9000,is_display_role=32,special_show=1,rarefloating=1,},
[23452]={id=23452,name="田单",pile_limit=200,use_type=79,param1=12,description="  鼓讁奔牛亦壮哉，一城力挽众城回。谁知辟土明封赏，即墨曾经培养来。",icon_id=23452,click_use=0,open_panel="FamousGeneralView#ming_jiang",drop_icon=23452,is_tip_use=0,choose_use=0,power=9000,is_display_role=32,special_show=1,rarefloating=1,},
[23453]={id=23453,name="乐毅",color=2,pile_limit=200,use_type=79,param1=0,description="  王道已沦昧，战国竞贪兵。乐生何感激，仗义下齐城。雄图竟中夭，遗叹寄阿衡。",icon_id=23453,click_use=0,open_panel="FamousGeneralView#ming_jiang",drop_icon=23453,is_tip_use=0,choose_use=0,power=9000,is_display_role=32,},
[23454]={id=23454,name="赵奢",color=3,pile_limit=200,use_type=79,description="  北山据险最能兵，中外俱闻马服名。满谓将门还出将，不知有子误长平。",icon_id=23454,click_use=0,open_panel="FamousGeneralView#ming_jiang",drop_icon=23454,is_tip_use=0,choose_use=0,power=18000,is_display_role=32,},
[23455]={id=23455,name="魏无忌",color=3,pile_limit=200,use_type=79,param1=2,description="  昔汉高钦无忌之义，魏武挹子干之风，前代名贤，后王斯重。",icon_id=23455,click_use=0,open_panel="FamousGeneralView#ming_jiang",drop_icon=23455,is_tip_use=0,choose_use=0,power=18000,is_display_role=32,},
[23456]={id=23456,name="田忌",pile_limit=200,use_type=79,param1=13,description="  当是之时，秦用商鞅，楚魏用吴起，齐用孙子、田忌。天下方务于合从连衡，以攻伐为贤。",icon_id=23456,click_use=0,open_panel="FamousGeneralView#ming_jiang",drop_icon=23456,is_tip_use=0,choose_use=0,power=18000,is_display_role=32,special_show=1,rarefloating=1,},
[23457]={id=23457,name="孙武",pile_limit=200,use_type=79,param1=14,description="  孙武兵经，辞如珠玉，岂以习武而不晓文也。",icon_id=23457,click_use=0,open_panel="FamousGeneralView#ming_jiang",drop_icon=23457,is_tip_use=0,choose_use=0,power=18000,is_display_role=32,special_show=1,rarefloating=1,},
[23458]={id=23458,name="王翦",color=4,pile_limit=200,use_type=79,param1=3,description="  少李轻兵去不回，荆人胜气鼓如雷。将军料敌元非怯，能使君王促驾来。",icon_id=23458,click_use=0,open_panel="FamousGeneralView#ming_jiang",drop_icon=23458,is_tip_use=0,choose_use=0,power=27000,is_display_role=32,},
[23459]={id=23459,name="蒙恬",color=4,pile_limit=200,use_type=79,param1=4,description="  绝漠功虽大，长城怨亦深。但知伤地脉，不悟失人心。",icon_id=23459,click_use=0,open_panel="FamousGeneralView#ming_jiang",drop_icon=23459,is_tip_use=0,choose_use=0,power=27000,is_display_role=32,},
[23460]={id=23460,name="廉颇",color=4,pile_limit=200,use_type=79,param1=5,description="  廉颇为人勇騺而爱士；知难而忍耻，与之野战则不如，持守足以当之。",icon_id=23460,click_use=0,open_panel="FamousGeneralView#ming_jiang",drop_icon=23460,is_tip_use=0,choose_use=0,power=27000,is_display_role=32,},
[23461]={id=23461,name="孙膑",color=4,pile_limit=200,use_type=79,param1=6,description="  曾嫌胜己害贤人，钻火明知速自焚。断足尔能行不足，逢君谁肯不酬君。",icon_id=23461,click_use=0,open_panel="FamousGeneralView#ming_jiang",drop_icon=23461,is_tip_use=0,choose_use=0,power=27000,is_display_role=32,},
