[103410] = { goods = { },npc = { s = { scene=10200,npc=10207},e = { scene=10200,npc=10207}},name=[[来        意]],lv=28,type=1,next=103420,exp=100,gold=0,interface=0,des=0,info=[[审问奥里奥]],say_start = { [1]={id=1,type=0,msg=[[喂！你叫我去大祭师那是干嘛的！]]},[2]={id=0,type=0,msg=[[哈啊。。你不是要找南疆法王算账吗 大祭师可是他的老婆啊]]},[3]={id=1,type=1,msg=[[什么！！！这。。。这。。信息量太大了，我想静静。]]}},say_end = { [1]={id=0,type=0,msg=[[呵呵。你这人真奇怪 连他们女儿的名字都知道了 却不知道他们俩的关系]]},[2]={id=1,type=0,msg=[[又什么女儿啊？]]},[3]={id=0,type=0,msg=[[你说的那条叫静静的蛇妖啊！那可是我们苗疆战乱的始作俑者嘞。]]},[4]={id=1,type=1,msg=[[。。。看来我还是低估了这个游戏的世界观了。。。得找个资深又正常的人了解下才行。]]}},say_ing = { [1]={id=0,type=0,msg=[[任务尚未完成，亲，你还要努力^@^]]}},target_1 = { },id=103410},
