[103710] = { goods = { },npc = { s = { scene=10200,npc=10201},e = { scene=10200,npc=10201}},name=[[人间兵器]],lv=37,type=1,next=103720,exp=200,gold=0,interface=0,des=0,info=[[听族长陈述]],say_start = { [1]={id=1,type=0,msg=[[为什么会是静静？]]},[2]={id=0,type=0,msg=[[因为她是大祭师的孩子。]]},[3]={id=0,type=0,msg=[[你有所不知,大祭师乃是接替女娲血脉的圣女,而且血脉不可传承。若被其他血脉玷污就会导致灭族之灾的降临。]]},[4]={id=0,type=0,msg=[[而这灭族之灾便是这人间兵器的诞生，这是一种不受人控制的怪物，她所拥有的破坏力，就如同她的体型一样巨大。]]},[5]={id=1,type=1,msg=[[那为什么不在她诞生前解决她？]]}},say_end = { [1]={id=0,type=0,msg=[[呵呵。。我们何曾不想，但若那时杀她,大祭师也会没命。]]},[2]={id=0,type=0,msg=[[若大祭师也死了，苗族就失去了信仰，没了信仰的种族跟灭族有什么区别！]]},[3]={id=0,type=0,msg=[[都是那龌蹉的法王，我恨不得将他碎尸万段。]]},[4]={id=1,type=1,msg=[[<龌蹉事件任务已屏蔽，请自行脑补>]]}},say_ing = { [1]={id=0,type=0,msg=[[任务尚未完成，亲，你还要努力^@^]]}},target_1 = { },id=103710},
