[113920] = { goods = { },npc = { s = { scene=11000,npc=11005},e = { scene=11000,npc=11001}},name=[[这不怪你]],lv=139,type=1,next=114210,exp=7200,gold=0,interface=0,des=[[挑战（#/1）次【烊铜地狱】副本]],info=[[找白无常]],say_start = { [1]={id=1,type=0,msg=[[唉～那事不怪你,你也只是尽你的本份而已。]]},[2]={id=0,type=1,msg=[[喂！喂！你别走啊！尽你妹的本份啊我又不是医生！]]}},say_end = { [1]={id=1,type=0,msg=[[我。。我怎么了。是不是。。挂了？]]},[2]={id=0,type=1,msg=[[嗯。。也差不多了。。现在只是饿晕。。再晚点就可能饿死了。。]]}},say_ing = { [1]={id=0,type=0,msg=[[任务尚未完成，亲，你还要努力^@^]]}},target_7 = { copy_id=11417,times=1},id=113920},
[114210] = { goods = { },npc = { s = { scene=11000,npc=11001},e = { scene=11000,npc=11001}},name=[[饿晕绝症]],lv=142,type=1,next=114220,exp=3700,gold=0,interface=0,des=0,info=[[找地藏王]],say_start = { [1]={id=1,type=0,msg=[[哦。还好。。等等。。你说我饿晕了。。难道不是绝症？]]},[2]={id=0,type=0,msg=[[如果厌食症也算是绝症的话,那你确实是染上了。]]},[3]={id=1,type=0,msg=[[厌食症？我没啊,我可想吃东西了!]]},[4]={id=0,type=0,msg=[[那你倒是去吃啊！]]},[5]={id=1,type=1,msg=[[那你倒是告诉我哪里有得吃呀！这地府除了人骨外就只有骨头了。]]},[6]=nil},say_end = { [1]={id=0,type=0,msg=[[啊～难道小白没带你去吃香喝蜡吗]]},[2]={id=1,type=0,msg=[[呦呵！原来还有辣的呀。。那有没有麻辣烫啊！]]},[3]={id=0,type=0,msg=[[我说的蜡是蜡烛的蜡。]]},[4]={id=1,type=0,msg=[[那你们的香也是。。。]]},[5]={id=0,type=1,msg=[[嗯。。是香烛的香。]]},[6]=nil},say_ing = { [1]={id=0,type=0,msg=[[任务尚未完成，亲，你还要努力^@^]]}},target_1 = { },id=114210},
[114220] = { goods = { },npc = { s = { scene=11000,npc=11001},e = { scene=11000,npc=11005}},name=[[地府有啥吃]],lv=142,type=1,next=114510,exp=7400,gold=0,interface=0,des=[[挑战（#/1）次【炼狱十九层】副本]],info=[[找地藏王]],say_start = { [1]={id=1,type=0,msg=[[所以说我这不就是得绝症的节奏吗？连吃的都没,跟绝症有区别吗！]]},[2]={id=1,type=1,msg=[[反正18层地狱我已经闯过了,趁我回光返照之际去见师傅最后一面吧。]]}},say_end = { [1]={id=1,type=0,msg=[[你！你你你！怎么不早告诉我,有贡品这种东西吃啊！]]},[2]={id=0,type=0,msg=[[我跟你说过,我说你有没有家属在这边的。]]},[3]={id=1,type=1,msg=[[这有关系？]]}},say_ing = { [1]={id=0,type=0,msg=[[任务尚未完成，亲，你还要努力^@^]]}},target_7 = { copy_id=11447,times=1},id=114220},
[114510] = { goods = { },npc = { s = { scene=11000,npc=11005},e = { scene=11000,npc=11005}},name=[[先人贡品]],lv=145,type=1,next=114520,exp=3800,gold=0,interface=0,des=0,info=[[找白无常]],say_start = { [1]={id=0,type=0,msg=[[贡品耶,你以为怎么来的。这都是后人给先人拜祭的贡品。]},[2]={id=0,type=0,msg=[[若是你有家属在这边,就可以找他们借点贡品补充下]},[3]={id=1,type=1,msg=[[这。。这会不会有点诡异哩,而且我可是孤儿。]}},say_end = { [1]={id=0,type=1,msg=[[唉～拿去这是我库存不多的贡品了,现在是少一个没一个啊。这次算我欠你的。]]}},say_ing = { [1]={id=0,type=0,msg=[[任务尚未完成，亲，你还要努力^@^]]}},target_1 = { },id=114510},
[114520] = { goods = { },npc = { s = { scene=11000,npc=11001},e = { scene=11000,npc=11001}},name=[[师傅疯了]],lv=145,type=1,next=114810,exp=7600,gold=0,interface=0,des=[[挑战（#/1）次【岩浆炼狱】副本]],info=[[找地藏王]],say_start = { [1]={id=1,type=0,msg=[[大师,我家师傅疯了。该怎么办啊！]]},[2]={id=0,type=0,msg=[[看来是真的把我的血给吸收了。若他只是疯了还好。]]},[3]={id=1,type=1,msg=[[还好？那如果不好的话会怎样?]]},[4]={id=0,type=0,msg=[[轻则心志异动,重则心肌衰竭,肌肉萎缩,神经衰老...]]},[5]={id=1,type=1,msg=[[够了,说得我都心肌颤抖了。。。不行我得去找他]]}},say_end = { [1]={id=1,type=0,msg=[[秃头,怎么你这地狱还有19层啊。]]},[2]={id=0,type=1,msg=[[哦～那是咱们鬼差的宿舍,难道你家师傅跑到那了。]]}},say_ing = { [1]={id=0,type=0,msg=[[任务尚未完成，亲，你还要努力^@^]]}},target_7 = { copy_id=11477,times=1},id=114520},
