    [214] = {20001,"关羽",12003,"0",23,20001,1,2,1,1,1,0,20002,0,0,0,0,5,2006,2,1,2,1,2000111,2000121,2000131,2000141,50012,4012,70112,0,0,0,0,0,2000101,2000102,2000103,2000104,2000105,2000106,2000107,2000108,2000109,2000110,2000111,2000112,24747,24736,24734,6963,279,615,25,0,0,379,15,316,13,1000,90,90,200011,200014,200012,10,200019,20067,20012,1,3000,"蜀汉五虎上将之首。勇武和义气的化身。温酒斩华雄，过五关斩六将，水淹七军，一生战绩无数！",6,"你死了别忘记我的名字，我就是关云长！",1,"voice/5_nan_guanyu.mp3","voice/J5_nan_guanyu.mp3","voice/2.mp3",10025,2000101,},
    [215] = {20012,"张飞",12004,"0",20,20012,1,2,1,1,1,0,20013,0,0,0,0,4,20022,2,1,2,1,2000113,2000132,2001211,2001221,70431,3032,50031,4032,0,0,0,0,2001201,2001202,2001203,2001204,2001205,2001206,2001207,2001208,2001209,2001210,2001211,2001212,24728,24717,24715,1719,69,152,6,0,0,93,4,78,3,1000,70,70,200121,200124,200122,30,0,20067,20001,1,3000,"蜀汉五虎上将之一，与刘备、关羽是桃园结义的兄弟，他性如烈火，嫉恶如仇，曾在长坂坡当阳桥一声怒吼，吓退十万曹军。",5,"俺乃燕人张翼德是也，谁敢与俺大战三百回合",1,"voice/5_nan_zhangfei.mp3","voice/J5_nan_zhangfei.mp3","voice/2.mp3",10026,2001201,},
    [216] = {20023,"赵云",12001,"0",20,20023,1,2,1,1,1,0,20024,0,0,0,0,4,20033,2,1,2,1,2000133,2002311,2002321,2002331,40041,3012,50041,4012,0,0,0,0,2002301,2002302,2002303,2002304,2002305,2002306,2002307,2002308,2002309,2002310,2002311,2002312,24725,24714,24721,1719,69,152,6,0,0,93,4,78,3,1000,70,70,200231,200234,200232,10,0,20045,0,1,3000,"蜀国五虎大将之一，以勇敢善战著称。曾在长坂坡七进七出百万曹军，救出刘备的儿子阿斗。",5,"常山赵子龙来也，吃我一枪！",1,"voice/5_nan_zhaoyun.mp3","voice/J5_nan_zhaoyun.mp3","voice/2.mp3",10027,2002301,},
    [217] = {20034,"马超",12005,"0",20,20034,1,2,1,1,1,0,20035,0,0,0,0,4,20044,2,1,2,1,2000134,2003411,2003421,2003431,60012,3012,50012,4012,0,0,0,0,2003401,2003402,2003403,2003404,2003405,2003406,2003407,2003408,2003409,2003410,2003411,2003412,24728,24717,24715,1719,69,152,6,0,0,93,4,78,3,1000,70,70,200341,200344,200342,10,0,20056,0,1,3000,"蜀汉五虎上将之一，马超作战勇猛非凡，曾多次击溃曹军进攻，让曹操也为之头疼。哀叹“马儿不死，吾无葬地也！”",5,"西凉马孟起在此，你且放马过来受死！",1,"voice/5_nan_machao.mp3","voice/J5_nan_machao.mp3","voice/2.mp3",10028,2003401,},
    [218] = {20045,"黄忠",12006,"0",20,20045,1,2,1,1,1,0,20046,0,0,0,0,4,20055,2,1,2,1,2000135,2002312,2004511,2004521,40212,3032,50012,4032,0,0,0,0,2004501,2004502,2004503,2004504,2004505,2004506,2004507,2004508,2004509,2004510,2004511,2004512,24728,24717,24715,1719,69,152,6,0,0,93,4,78,3,1000,70,70,200451,200454,200452,20,0,20023,0,1,3000,"蜀汉五虎上将之一，有勇有谋，老当益壮，箭术百步穿杨，汉中之战时，他在定军山将曹魏大将夏侯渊砍成两截，乘胜夺取天荡山！",5,"黄忠虽老，还能吃十斤肉，开千石弓！",1,"voice/5_nan_huangzhong.mp3","voice/J5_nan_huangzhong.mp3","voice/2.mp3",10029,2004501,},
