    [227] = {20144,"黄月英",12015,"0",18,20144,0,2,2,1,1,0,20145,0,0,0,0,3,20154,3,4,2,1,4003432,2013312,2014411,2014421,30212,2021,0,0,0,0,0,0,2014401,2014402,2014403,2014404,2014405,2014406,2014407,2014408,0,0,0,0,0,0,0,1520,61,0,0,126,5,68,3,83,3,1000,40,40,201441,201444,201442,20,0,40034,0,1,3000,"诸葛亮之妻，她知识极其广博，熟读兵书，上知天文，下知地理，诸葛亮发明木牛流马，相传是从她传授的技巧上发展出来。",4,"孔明大人，这样可以算帮到您了吗？",1,"voice/5_nv_huangyueying.mp3","voice/J5_nv_huangyueying.mp3","voice/6.mp3",10038,0,},
    [228] = {20155,"徐庶",12016,"0",20,20155,1,2,2,1,1,0,20156,0,0,0,0,4,20165,2,2,2,1,4013322,4018822,2008912,2014423,40012,3041,50012,4041,0,0,0,0,2015501,2015502,2015503,2015504,2015505,2015506,2015507,2015508,2015509,2015510,2015511,2015512,24725,24714,24721,1719,69,0,0,152,6,77,3,93,4,1000,70,70,201551,201554,201552,20,0,20089,0,1,3000,"蜀汉谋士，他与司马徽、诸葛亮、崔州平等人为友。刘备屯驻新野时，徐庶前往投奔，并向刘备推荐诸葛亮。",5,"水无常势，兵无常形。我的实力你不懂！",1,"voice/5_nan_xushu.mp3","voice/J5_nan_xushu.mp3","voice/2.mp3",10039,2015501,},
