    [738] = {30002,"孙坚",13003,"0",23,30001,1,2,1,1,1,1,30003,0,0,0,0,5,2008,2,1,3,0,3000111,3000121,3000131,3000141,50012,4032,0,0,0,0,0,0,3000101,3000102,3000103,3000104,3000105,3000106,3000107,3000108,3000109,3000110,3000111,3000112,24747,24736,24734,8073,323,691,28,0,0,440,18,366,15,1000,90,90,300011,300014,300012,10,300019,30089,0,1,3000,"东吴始祖武烈皇帝，东汉末期地方军阀，吴国霸业奠基人，其人容貌不凡，性格豪爽阔达。曾参与讨伐黄巾军的战役以及讨伐董卓的战役。",6,"董卓，成为我刀上的血锈吧！！",1,"voice/5_nan_sunjian.mp3","voice/J5_nan_sunjian.mp3","voice/3.mp3",10048,3000101,},
    [739] = {30013,"孙策",13004,"0",20,30012,1,2,1,1,1,1,30014,0,0,0,0,4,30022,2,1,3,0,3000132,3004522,3001211,3003412,40221,3032,50021,4032,0,0,0,0,3001201,3001202,3001203,3001204,3001205,3001206,3001207,3001208,3001209,3001210,3001211,3001212,24728,24717,24715,1979,79,175,7,0,0,107,4,89,4,1000,70,70,300121,300124,300122,20,0,30034,0,1,3000,"孙坚长子，孙权长兄。绰号“小霸王”，东汉末年割据江东一带的军阀，汉末群雄之一，三国时期吴国的奠基者之一。",5,"能粉碎这乱世的，只有我孙策啊！",1,"voice/5_nan_sunce.mp3","voice/J5_nan_sunce.mp3","voice/3.mp3",10049,3001201,},
    [740] = {30024,"孙权",13005,"0",20,30023,1,2,2,1,1,1,30025,0,0,0,0,4,30033,2,2,3,0,3005612,3006732,3002311,3002321,40021,3012,50021,4012,0,0,0,0,3002301,3002302,3002303,3002304,3002305,3002306,3002307,3002308,3002309,3002310,3002311,3002312,24725,24714,24721,1979,79,0,0,175,7,89,4,108,4,1000,70,70,300231,300234,300232,20,0,30056,0,1,3000,"吴国开国皇帝，幼年随兄长孙策平定江东，后继位为江东之主，他结盟蜀汉，击败魏国，仁贤用能、挽救江东危局，保住了父兄基业。",5,"阻我大业者，便是神佛，也要他灰飞烟灭！",1,"voice/5_nan_sunquan.mp3","voice/J5_nan_sunquan.mp3","voice/3.mp3",10050,3002301,},
    [741] = {30035,"太史慈",13006,"0",20,30034,1,2,1,1,1,1,30036,0,0,0,0,4,30044,2,1,3,0,2004512,3003411,3003421,3003431,40212,3012,50012,4012,0,0,0,0,3003401,3003402,3003403,3003404,3003405,3003406,3003407,3003408,3003409,3003410,3003411,3003412,24728,24717,24715,1979,79,175,7,0,0,107,4,89,4,1000,70,70,300341,300344,300342,10,0,30012,0,1,3000,"东吴大将，官至建昌都尉，为人磊落坦荡，尽义守信，智勇双全，曾与孙策对拼数战而不败。",5,"大丈夫，当带三尺之剑，立不世之功。",1,"voice/5_nan_taishici.mp3","voice/J5_nan_taishici.mp3","voice/3.mp3",10051,3003401,},
    [742] = {30046,"周瑜",13007,"0",23,30045,1,2,2,1,1,1,30047,0,0,0,0,5,2009,2,2,3,0,3000122,3004511,3004521,3004531,50041,4012,70041,0,0,0,0,0,3004501,3004502,3004503,3004504,3004505,3004506,3004507,3004508,3004509,3004510,3004511,3004512,24744,24733,24740,8007,320,0,0,708,28,363,15,437,17,1000,90,90,300451,300454,300452,10,300459,30144,0,1,3000,"东吴大将，因其相貌英俊而有“周郎”之称。他精通军事，又精于音律，江东向来有“曲有误，周郎顾”之语。",6,"既生瑜，何生亮？",1,"voice/5_nan_zhouyu.mp3","voice/J5_nan_zhouyu.mp3","voice/3.mp3",10052,3004501,},
    [743] = {30057,"鲁肃",13002,"0",20,30056,1,2,2,1,1,1,30058,0,0,0,0,4,30066,2,2,3,0,3004532,3005611,3005621,3005631,40012,3041,50012,4041,0,0,0,0,3005601,3005602,3005603,3005604,3005605,3005606,3005607,3005608,3005609,3005610,3005611,3005612,24725,24714,24721,1979,79,0,0,175,7,89,4,108,4,1000,70,70,300561,300564,300562,10,0,30023,0,1,3000,"东吴著名统帅。杰出战略家，著名政治家、外交家。曾为孙权提出鼎足江东的战略规划，因此得到孙权赏识！",5,"孙刘联合，是我这辈子最得意的成就！",1,"voice/5_nan_lusu.mp3","voice/J5_nan_lusu.mp3","voice/3.mp3",10053,3005601,},
    [744] = {30068,"吕蒙",13008,"0",20,30067,1,2,2,1,1,1,30069,0,0,0,0,4,30077,2,2,3,0,1001222,3006711,3006721,3006731,40212,3032,50012,4032,0,0,0,0,3006701,3006702,3006703,3006704,3006705,3006706,3006707,3006708,3006709,3006710,3006711,3006712,24728,24717,24715,1979,79,0,0,175,7,89,4,108,4,1000,70,70,300671,300674,300672,10,0,30078,0,1,3000,"东吴大将，被封为虎威将军，曾趁名将关羽北伐曹操、荆州空虚时，偷袭荆州成功，使东吴国土面积大增。",5,"士别三日，当刮目相看。",1,"voice/5_nan_lvmeng.mp3","voice/J5_nan_lvmeng.mp3","voice/3.mp3",10054,3006701,},
