    [1165] = {20125,"真·马良",12013,"0",18,20122,1,2,2,1,1,3,20126,0,0,0,0,3,20132,3,2,2,0,2012211,2012221,2012231,2016632,30012,2041,0,0,0,0,0,0,2012201,2012202,2012203,2012204,2012205,2012206,2012207,2012208,0,0,0,0,0,0,0,2313,93,0,0,192,8,104,4,126,5,1000,40,40,201221,0,201222,10,0,0,0,1,3000,"蜀汉官员。马良兄弟五人都有才华名气，而马良又在五人中最为出色，因此有“马氏五常，白眉最良”的赞誉。",4,"下智伐人，上智攻心。你的伎俩，我已全数识破。",1,"voice/5_nan_maliang.mp3","voice/J5_nan_maliang.mp3","voice/2.mp3",10036,0,},
    [1166] = {20136,"真·夏侯涓",12014,"0",18,20133,1,2,1,1,1,3,20137,0,0,0,0,3,20143,3,1,2,0,2001212,1003422,2013311,2013321,30021,2012,0,0,0,0,0,0,2013301,2013302,2013303,2013304,2013305,2013306,2013307,2013308,0,0,0,0,0,0,0,2313,93,192,8,0,0,126,5,105,4,1000,40,40,201331,0,201332,10,0,0,0,1,3000,"夏侯渊侄女，夏侯霸堂妹，在外出采桑，为张飞所得，从此成为张飞的妻子。生二子二女；其二女皆为蜀汉后主皇后。",4,"执子之手，与子携老。",1,"voice/5_nv_xiahoujuan.mp3","voice/J5_nv_xiahoujuan.mp3","voice/6.mp3",10037,0,},
    [1167] = {20147,"真·黄月英",12015,"0",18,20144,0,2,2,1,1,3,20148,0,0,0,0,3,20154,3,4,2,0,4003432,2013312,2014411,2014421,30212,2021,0,0,0,0,0,0,2014401,2014402,2014403,2014404,2014405,2014406,2014407,2014408,0,0,0,0,0,0,0,2313,93,0,0,192,8,104,4,126,5,1000,40,40,201441,201444,201442,20,0,40034,0,1,3000,"诸葛亮之妻，她知识极其广博，熟读兵书，上知天文，下知地理，诸葛亮发明木牛流马，相传是从她传授的技巧上发展出来。",4,"孔明大人，这样可以算帮到您了吗？",1,"voice/5_nv_huangyueying.mp3","voice/J5_nv_huangyueying.mp3","voice/6.mp3",10038,0,},
    [1168] = {20158,"真·徐庶",12016,"0",20,20155,1,2,2,1,1,3,20159,0,0,0,0,4,20165,2,2,2,0,4013322,4018822,2008912,2014423,40012,3041,50012,4041,0,0,0,0,2015501,2015502,2015503,2015504,2015505,2015506,2015507,2015508,2015509,2015510,2015511,2015512,24725,24714,24721,2616,105,0,0,231,9,118,5,142,6,1000,70,70,201551,201554,201552,20,0,20089,0,1,3000,"蜀汉谋士，他与司马徽、诸葛亮、崔州平等人为友。刘备屯驻新野时，徐庶前往投奔，并向刘备推荐诸葛亮。",5,"水无常势，兵无常形。我的实力你不懂！",1,"voice/5_nan_xushu.mp3","voice/J5_nan_xushu.mp3","voice/2.mp3",10039,2015501,},
    [1169] = {20169,"真·关兴",12017,"0",18,20166,1,2,1,1,1,3,20170,0,0,0,0,3,20176,2,3,2,0,2011112,2016611,2016621,2016631,30123,2021,0,0,0,0,0,0,2016601,2016602,2016603,2016604,2016605,2016606,2016607,2016608,0,0,0,0,0,0,0,2277,91,201,8,0,0,124,5,103,4,1000,40,40,201661,201664,201662,10,0,20177,0,1,3000,"蜀汉将领，名将关羽次子，官至龙骧将军，如其父关羽般勇猛，他与张飞之子张苞结拜为弟兄，随刘备伐吴，并深受诸葛亮器重。",4,"战斗，是我生命的一部分！",1,"voice/5_nan_guanxing.mp3","voice/J5_nan_guanxing.mp3","voice/2.mp3",10040,0,},
