    [1207] = {30026,"真·孙权",130052,"0",20,30023,1,2,2,1,1,3,30027,0,0,0,0,4,30033,2,2,3,0,3005612,3006732,3002311,3002321,40021,3012,50021,4012,0,0,0,0,3002301,3002302,3002303,3002304,3002305,3002306,3002307,3002308,3002309,3002310,3002311,3002312,24725,24714,24721,2616,105,0,0,231,9,118,5,142,6,1000,70,70,300231,300234,300232,20,0,30056,0,1,3000,"吴国开国皇帝，幼年随兄长孙策平定江东，后继位为江东之主，他结盟蜀汉，击败魏国，仁贤用能、挽救江东危局，保住了父兄基业。",5,"阻我大业者，便是神佛，也要他灰飞烟灭！",1,"voice/5_nan_sunquan.mp3","voice/J5_nan_sunquan.mp3","voice/3.mp3",10050,3002301,},
