    [1612] = {20006,"真·关羽",120032,"0",23,20001,1,2,1,1,1,5,20007,0,0,0,0,5,2006,2,1,2,0,2000111,2000121,2000131,2000141,50012,4012,70112,0,0,0,0,0,2000101,2000102,2000103,2000104,2000105,2000106,2000107,2000108,2000109,2000110,2000111,2000112,24747,24736,24734,14002,560,1238,50,0,0,763,31,636,25,1000,90,90,200011,200014,200012,10,200019,20067,20012,1,3000,"蜀汉五虎上将之首。勇武和义气的化身。温酒斩华雄，过五关斩六将，水淹七军，一生战绩无数！",6,"你死了别忘记我的名字，我就是关云长！",1,"voice/5_nan_guanyu.mp3","voice/J5_nan_guanyu.mp3","voice/2.mp3",10025,2000101,},
