    [1616] = {20050,"真·黄忠",120062,"0",20,20045,1,2,1,1,1,5,20051,0,0,0,0,4,20055,2,1,2,0,2000135,2002312,2004511,2004521,40212,3032,50012,4032,0,0,0,0,2004501,2004502,2004503,2004504,2004505,2004506,2004507,2004508,2004509,2004510,2004511,2004512,24728,24717,24715,3459,138,305,12,0,0,188,8,157,6,1000,70,70,200451,200454,200452,20,0,20023,0,1,3000,"蜀汉五虎上将之一，有勇有谋，老当益壮，箭术百步穿杨，汉中之战时，他在定军山将曹魏大将夏侯渊砍成两截，乘胜夺取天荡山！",5,"黄忠虽老，还能吃十斤肉，开千石弓！",1,"voice/5_nan_huangzhong.mp3","voice/J5_nan_huangzhong.mp3","voice/2.mp3",10029,2004501,},
