    [1892] = {30007,"无双·孙坚",130032,"0",23,30001,1,2,1,1,1,6,30008,0,0,0,0,5,2008,2,1,3,0,3000111,3000121,3000131,3000141,50012,4032,0,0,0,0,0,0,3000101,3000102,3000103,3000104,3000105,3000106,3000107,3000108,3000109,3000110,3000111,3000112,24747,24736,24734,16234,649,1391,56,0,0,885,35,737,29,1000,90,90,300011,300014,300012,10,300019,30089,0,1,3000,"东吴始祖武烈皇帝，东汉末期地方军阀，吴国霸业奠基人，其人容貌不凡，性格豪爽阔达。曾参与讨伐黄巾军的战役以及讨伐董卓的战役。",6,"董卓，成为我刀上的血锈吧！！",1,"voice/5_nan_sunjian.mp3","voice/J5_nan_sunjian.mp3","voice/3.mp3",10048,3000101,},
