    [2594] = {30175,"圣·黄盖",13016,"0",18,30166,1,2,1,1,1,9,30176,0,0,0,0,3,30176,1,1,3,0,3005632,3010012,3016611,3016621,30012,2032,0,0,0,0,0,0,3016601,3016602,3016603,3016604,3016605,3016606,3016607,3016608,0,0,0,0,0,0,0,5308,212,454,18,0,0,265,11,265,11,1000,40,40,301661,301664,301662,10,0,30177,0,1,3000,"东吴大将，历仕孙坚、孙策、孙权三任君主。火烧赤壁时，他施展苦肉计前往曹营诈降，并趁机火烧曹军，是赤壁之战主要功臣之一。",4,"有种你就鞭挞我吧 ，周瑜小儿！",1,"voice/5_nan_huanggai.mp3","voice/J5_nan_huanggai.mp3","voice/3.mp3",10063,0,},
