    [3000] = {20703,"霸·张飞",120043,"0",20,20012,1,2,1,1,1,11,20704,0,20836,0,16,4,20022,2,1,2,0,2000113,2000132,2001211,2001221,70431,3032,50031,4032,0,0,0,0,2001201,2001202,2001203,2001204,2001205,2001206,2001207,2001208,2001209,2001210,2001211,2001212,24728,24717,24715,7997,319,707,28,0,0,435,17,363,14,1000,70,70,200121,200124,200122,30,0,20067,20001,1,3000,"蜀汉五虎上将之一，与刘备、关羽是桃园结义的兄弟，他性如烈火，嫉恶如仇，曾在长坂坡当阳桥一声怒吼，吓退十万曹军。",5,"俺乃燕人张翼德是也，谁敢与俺大战三百回合",1,"voice/5_nan_zhangfei.mp3","voice/J5_nan_zhangfei.mp3","voice/2.mp3",10026,2001201,},
    [3001] = {20705,"霸·赵云",120013,"0",20,20023,1,2,1,1,1,11,20706,0,20866,0,16,4,20033,2,1,2,0,2000133,2002311,2002321,2002331,40041,3012,50041,4012,0,0,0,0,2002301,2002302,2002303,2002304,2002305,2002306,2002307,2002308,2002309,2002310,2002311,2002312,24725,24714,24721,7997,319,707,28,0,0,435,17,363,14,1000,70,70,200231,200234,200232,10,0,20045,0,1,3000,"蜀国五虎大将之一，以勇敢善战著称。曾在长坂坡七进七出百万曹军，救出刘备的儿子阿斗。",5,"常山赵子龙来也，吃我一枪！",1,"voice/5_nan_zhaoyun.mp3","voice/J5_nan_zhaoyun.mp3","voice/2.mp3",10027,2002301,},
    [3002] = {20707,"霸·马超",120053,"0",20,20034,1,2,1,1,1,11,20708,0,20896,0,16,4,20044,2,1,2,0,2000134,2003411,2003421,2003431,60012,3012,50012,4012,0,0,0,0,2003401,2003402,2003403,2003404,2003405,2003406,2003407,2003408,2003409,2003410,2003411,2003412,24728,24717,24715,7997,319,707,28,0,0,435,17,363,14,1000,70,70,200341,200344,200342,10,0,20056,0,1,3000,"蜀汉五虎上将之一，马超作战勇猛非凡，曾多次击溃曹军进攻，让曹操也为之头疼。哀叹“马儿不死，吾无葬地也！”",5,"西凉马孟起在此，你且放马过来受死！",1,"voice/5_nan_machao.mp3","voice/J5_nan_machao.mp3","voice/2.mp3",10028,2003401,},
    [3003] = {20709,"霸·黄忠",120063,"0",20,20045,1,2,1,1,1,11,20710,0,20926,0,16,4,20055,2,1,2,0,2000135,2002312,2004511,2004521,40212,3032,50012,4032,0,0,0,0,2004501,2004502,2004503,2004504,2004505,2004506,2004507,2004508,2004509,2004510,2004511,2004512,24728,24717,24715,7997,319,707,28,0,0,435,17,363,14,1000,70,70,200451,200454,200452,20,0,20023,0,1,3000,"蜀汉五虎上将之一，有勇有谋，老当益壮，箭术百步穿杨，汉中之战时，他在定军山将曹魏大将夏侯渊砍成两截，乘胜夺取天荡山！",5,"黄忠虽老，还能吃十斤肉，开千石弓！",1,"voice/5_nan_huangzhong.mp3","voice/J5_nan_huangzhong.mp3","voice/2.mp3",10029,2004501,},
    [3004] = {20711,"霸·魏延",12007,"0",20,20056,1,2,1,1,1,11,20712,0,20956,0,16,4,20066,2,1,2,0,2007832,2003412,2005611,2005621,40221,3032,50021,4032,0,0,0,0,2005601,2005602,2005603,2005604,2005605,2005606,2005607,2005608,2005609,2005610,2005611,2005612,24728,24717,24715,7997,319,707,28,0,0,435,17,363,14,1000,70,70,200561,200564,200562,20,0,20034,0,1,3000,"蜀汉名将，因屡立战功而深受刘备信任，蜀汉建立后升为镇北将军，领汉中太守，镇守汉中，成为独当一方的大将。",5,"谁敢杀我？谁敢杀我？谁敢杀我？",1,"voice/5_nan_weiyan.mp3","voice/J5_nan_weiyan.mp3","voice/2.mp3",10030,2005601,},
    [3005] = {20713,"霸·刘备",120083,"0",20,20067,1,2,1,1,1,11,20714,0,20986,0,25,4,20077,3,4,2,0,2000112,2007822,2002322,2006711,60012,3041,50012,4041,0,0,0,0,2006701,2006702,2006703,2006704,2006705,2006706,2006707,2006708,2006709,2006710,2006711,2006712,24722,24717,24721,8120,324,676,27,0,0,442,17,368,14,1000,70,70,200671,200674,200672,20,0,20001,20012,1,3000,"三国时蜀汉开国皇帝，他为人谦和，礼贤下士，素以仁德为世人称赞，并以此吸引无数人才来投，最终建立三分天下的蜀汉政权。",5,"备一生谨记：要以德服人，以德服人啊！",1,"voice/5_nan_liubei.mp3","voice/J5_nan_liubei.mp3","voice/2.mp3",10031,2006701,},
    [3006] = {20715,"霸·诸葛亮",120092,"0",23,20078,1,2,2,1,1,11,20716,0,21007,0,22,5,2007,2,2,2,0,2000142,2007811,2007821,2007831,50041,4032,0,0,0,0,0,0,2007801,2007802,2007803,2007804,2007805,2007806,2007807,2007808,2007809,2007810,2007811,2007812,24744,24733,24740,32384,1294,0,0,2864,115,1468,58,1768,71,1000,90,90,200781,200784,200783,10,200789,20111,0,1,3000,"蜀汉丞相，号卧龙，是忠臣和智慧的化身，曾舌战群儒促成吴蜀联盟，空城计吓退司马懿，一生为蜀国事业鞠躬尽瘁，死而后已。",6,"亮略施小计，烧你八十三万大军！",1,"voice/5_nan_zhugeliang.mp3","voice/J5_nan_zhugeliang.mp3","voice/2.mp3",10032,2007801,},
