    [4272] = {30931,"圣·鲁肃",130024,"0",23,30056,1,2,2,1,1,9,30937,5,30932,30930,21,4,2004,2,2,3,0,3004532,3005611,3005621,3005631,40012,3041,50012,4041,0,0,0,0,23301,23302,23303,23304,23305,23306,23307,23308,23309,23310,23311,23312,24744,24733,24740,24459,978,0,0,2160,86,1108,44,1335,53,1000,70,70,300561,3005614,3005612,10,3005619,30023,0,1,3000,"东吴著名统帅。杰出战略家，著名政治家、外交家。曾为孙权提出鼎足江东的战略规划，因此得到孙权赏识！",6,"孙刘联合，是我这辈子最得意的成就！",1,"voice/5_nan_lusu.mp3","voice/J5_nan_lusu.mp3","voice/3.mp3",10053,3005601,},
