    [5006] = {21108,"王·魏延",12007,"0",20,20056,1,2,1,1,1,13,21159,0,21261,0,16,4,20066,2,1,2,0,2007832,2003412,2005611,2005621,40221,3032,50021,4032,0,0,0,0,2005601,2005602,2005603,2005604,2005605,2005606,2005607,2005608,2005609,2005610,2005611,2005612,24728,24717,24715,10575,423,934,37,0,0,575,23,479,19,1000,70,70,200561,200564,200562,20,0,20034,0,1,3000,"蜀汉名将，因屡立战功而深受刘备信任，蜀汉建立后升为镇北将军，领汉中太守，镇守汉中，成为独当一方的大将。",5,"谁敢杀我？谁敢杀我？谁敢杀我？",1,"voice/5_nan_weiyan.mp3","voice/J5_nan_weiyan.mp3","voice/2.mp3",10030,2005601,},
    [5007] = {21109,"王·刘备",120083,"0",20,20067,1,2,1,1,1,13,21160,0,21262,0,25,4,20077,3,4,2,0,2000112,2007822,2002322,2006711,60012,3041,50012,4041,0,0,0,0,2006701,2006702,2006703,2006704,2006705,2006706,2006707,2006708,2006709,2006710,2006711,2006712,24722,24717,24721,10738,430,893,36,0,0,584,23,486,19,1000,70,70,200671,200674,200672,20,0,20001,20012,1,3000,"三国时蜀汉开国皇帝，他为人谦和，礼贤下士，素以仁德为世人称赞，并以此吸引无数人才来投，最终建立三分天下的蜀汉政权。",5,"备一生谨记：要以德服人，以德服人啊！",1,"voice/5_nan_liubei.mp3","voice/J5_nan_liubei.mp3","voice/2.mp3",10031,2006701,},
    [5008] = {21110,"王·诸葛亮",120092,"0",23,20078,1,2,2,1,1,13,21161,0,21263,0,22,5,2007,2,2,2,0,2000142,2007811,2007821,2007831,50041,4032,0,0,0,0,0,0,2007801,2007802,2007803,2007804,2007805,2007806,2007807,2007808,2007809,2007810,2007811,2007812,24744,24733,24740,42827,1713,0,0,3786,151,1941,78,2337,93,1000,90,90,200781,200784,200783,10,200789,20111,0,1,3000,"蜀汉丞相，号卧龙，是忠臣和智慧的化身，曾舌战群儒促成吴蜀联盟，空城计吓退司马懿，一生为蜀国事业鞠躬尽瘁，死而后已。",6,"亮略施小计，烧你八十三万大军！",1,"voice/5_nan_zhugeliang.mp3","voice/J5_nan_zhugeliang.mp3","voice/2.mp3",10032,2007801,},
