    [5108] = {21210,"至尊·魏延",12007,"0",20,20056,1,2,1,1,1,15,0,0,21283,0,16,4,20066,2,1,2,0,2007832,2003412,2005611,2005621,40221,3032,50021,4032,0,0,0,0,2005601,2005602,2005603,2005604,2005605,2005606,2005607,2005608,2005609,2005610,2005611,2005612,24728,24717,24715,13985,559,1236,49,0,0,760,30,634,25,1000,70,70,200561,200564,200562,20,0,20034,0,1,3000,"蜀汉名将，因屡立战功而深受刘备信任，蜀汉建立后升为镇北将军，领汉中太守，镇守汉中，成为独当一方的大将。",5,"谁敢杀我？谁敢杀我？谁敢杀我？",1,"voice/5_nan_weiyan.mp3","voice/J5_nan_weiyan.mp3","voice/2.mp3",10030,2005601,},
