    [5121] = {21223,"至尊·祝融",12002,"0",18,20199,0,2,1,1,1,15,0,0,0,0,0,3,20209,2,1,2,0,2013322,2018812,2019911,2019921,30012,2021,0,0,0,0,0,0,2019901,2019902,2019903,2019904,2019905,2019906,2019907,2019908,0,0,0,0,0,0,0,12173,487,1075,43,0,0,661,26,552,22,1000,40,40,201991,201994,201992,20,0,20188,0,1,3000,"南蛮王孟获之妻，别名刺美人,传说为火神祝融氏后裔，她武艺高强，善使飞刀，曾与孟获一起抵抗蜀国军队。",4,"与我南蛮勇士比，北人真是弱不禁风啊！",1,"voice/5_nv_zhurong.mp3","voice/J5_nv_zhurong.mp3","voice/6.mp3",10043,0,},
