    [998] = {1020,1,"将军的实力已经很强了，可以去跟天下英雄一起夺宝了！",19003,1,0,0,0,0,0,19003,0,0,1,"ui/background/back_mainbt.png","0","","","0","0",},
    [999] = {1020,2,"宝物能够大量增加武将的生命和攻击，是非常强大的宝贝哦。",19003,1,0,0,0,0,0,19003,0,45,1,"ui/background/back_mainbt.png","0","","","0","0",},
