    [1001] = {1021,1,"有两种宝物：虎符能增加生命，兵书能增加攻击，凑齐宝物碎片就能合成宝物。",19003,1,0,0,0,0,0,19003,0,0,1,"ui/background/duobao.png","0","","","0","0",},
