    [1019] = {1024,2,"每次排名提升都会获得元宝奖励，并且当您的排名越高，每天获得的声望奖励就越多。",19003,1,0,0,0,0,0,19003,0,45,1,"ui/background/jingjichang.png","0","","","0","0",},
