    [1061] = {1051,2,"将军，三国志残卷中蕴含着命运的星宿之力，可以让将军变得异常的强大！",19003,1,0,0,0,0,0,19003,0,45,1,"ui/dungeon/chapterbg.png","0","","","0","0",},
