    [1088] = {1067,1,"恭喜将军拥有了自己的领地！在已占领的领地里，将军您可以派遣紫色以上的武将进行巡逻哦。",19003,1,0,0,0,0,0,19003,0,0,1,"ui/city/bg_taofa.png","0","","","0","0",},
    [1089] = {1067,2,"巡逻会耗费精力和4~12小时不等的现实时间，武将会在这期间遇见各种事件，从中获得各种资源奖励。",19003,1,0,0,0,0,0,19003,0,0,1,"ui/city/bg_taofa.png","0","","","0","0",},
