    [1097] = {1070,2,"武将巡逻可以获得武将碎片、体力丹等丰厚资源，请去获取一些高品质的武将再来吧！",19003,1,0,0,0,0,0,19003,0,0,1,"ui/city/bg_taofa.png","0","","","0","0",},
    [1098] = {1071,1,"将军，菜菜为您精心准备了一个大礼包，使用后可以获取一组有合击技能的紫色武将~怎么样，是不是很贴心呢？",19003,1,0,0,0,0,0,19003,0,0,1,"ui/dungeon/chapterbg.png","0","","","0","0",},
