    [1102] = {1072,2,"可是菜菜，我现在只能上阵2名武将，没办法把你们全都安置到队伍里啊！",1,1,0,0,0,0,0,19003,0,0,1,"ui/background/bg_common.png","0","","","0","0",},
    [1103] = {1072,3,"将军放心！等您升到【9级】、【16级】时，就会分别开启第三、第四个队伍位置，到时候你就能放下两组合击武将了！",19003,1,0,0,0,0,0,19003,0,0,1,"ui/background/bg_common.png","0","","","0","0",},
    [1104] = {1072,4,"现在就先让英英菜菜陪着你，去消灭作乱的黄巾军吧~",19003,1,0,0,0,0,0,19003,0,0,1,"ui/background/bg_common.png","0","","","0","0",},
    [1105] = {2021,1,"将军，黄巾军领袖张角张梁张宝三兄弟都不好对付，我们当心点。",19005,1,0,0,0,0,0,19005,0,0,1,"pic/stagemap/5.png","0","","","0","0",},
