    [2196] = {2591,3,"曹军又追杀过来了，小云云，我们得加快速度撤退才行。",1,1,0,34,0,0,0,11358,0,0,1,"pic/stagemap/10.png","0","","","0","0",},
    [2197] = {2592,1,"将军，前方就是长板桥，过了桥就是刘备的临时驻扎地了。",19005,1,0,0,0,0,0,19005,0,0,1,"pic/stagemap/10.png","0","","","0","0",},
    [2198] = {2592,2,"不好，长板桥居然被曹军围攻了，我们得赶紧去帮忙。",1,1,0,26,0,0,0,19005,0,0,1,"pic/stagemap/10.png","0","","","0","0",},
    [2199] = {2592,3,"想去帮忙？先想想你们自己怎么受死吧！来人，给我将他们抓起来！",11286,1,0,0,0,0,0,11286,0,29,1,"pic/stagemap/10.png","0","","","0","0",},
    [2200] = {2593,1,"吓尿了，吓尿了，丞相，刚刚有人一声大叫，吓得我辣条都掉了！",11358,11358,0,33,0,0,0,11173,0,0,1,"pic/stagemap/10.png","0","","","0","0",},
