    [2203] = {2593,4,"咦？长板桥的曹军怎么突然朝后撤退了。",1,1,0,50,0,0,0,11173,0,0,1,"pic/stagemap/10.png","0","","","0","0",},
    [2204] = {2593,5,"卧槽，又是你这家伙！你们就是张飞那小子安排的伏兵吗？来人，给我杀了他们！",11173,1,0,0,0,0,0,11173,0,27,1,"pic/stagemap/10.png","0","","","0","0",},
    [2205] = {2594,1,"呼呼…幸好我们冲出重围，不然真要被曹操的人马踩死了……",19003,1,0,0,0,0,0,19003,0,47,1,"0","0","","","0","0",},
    [2206] = {2594,2,"听曹操的意思，张飞似乎就在前面，我们快去看看怎么回事！",1,1,0,0,0,0,0,19003,0,0,1,"0","0","","","0","0",},
    [2207] = {2595,1,"啊！前面是张飞，他在干嘛！干嘛把桥拆了？",1,1,0,34,0,0,0,10940,0,0,1,"pic/stagemap/10.png","0","","","0","0",},
    [2208] = {2595,2,"哈哈，曹操那些人，胆小如鼠，刚刚我单枪匹马站桥上大吼几声，就把他们吓回去了~",10940,1,0,0,0,0,0,10940,0,5,1,"pic/stagemap/10.png","0","","","0","0",},
    [2209] = {2595,3,"我现在把桥拆了，好让他过不来追咱们，快夸我聪明，哈哈！",10940,1,0,0,0,0,0,10940,0,53,1,"pic/stagemap/10.png","0","","","0","0",},
    [2210] = {2595,4,"噗~说你蠢萌你还真蠢萌，你把桥拆了，曹操派个探子回来看，肯定立马返回来追咱们~",19003,19003,0,7,0,0,0,10940,0,0,1,"pic/stagemap/10.png","0","","","0","0",},
    [2211] = {2595,5,"太过分啦！居然说我蠢，别以为跟我大哥有交情我就不会揍你！",10940,19003,0,0,0,0,0,10940,0,28,1,"pic/stagemap/10.png","0","","","0","0",},
    [2212] = {2596,1,"不打了不打了！……我桥都拆了，你说怎么办！难不成重新建回去吗！",10940,1,0,0,0,0,0,10940,0,13,1,"0","0","","","0","0",},
