    [2453] = {2701,2,"咦？他本来不是好好的吗？怎么突然就生病了。",1,1,0,50,0,0,0,11399,0,0,1,"pic/stagemap/11.png","0","","","0","0",},
    [2454] = {2701,3,"据说是因为打麻将，老抽不到东风，一直输牌给气的。",11399,1,0,0,0,0,0,11399,0,25,1,"pic/stagemap/11.png","0","","","0","0",},
