    [2457] = {2702,1,"小瑜瑜你不就是担心没东风嘛，我给你把风招来。",11063,1,0,0,0,0,0,11063,0,53,1,"pic/stagemap/11.png","0","","","0","0",},
    [2458] = {2702,2,"你还会呼风唤雨？你又不是道士，怎么会这个？",1,1,0,19,0,0,0,11063,0,0,1,"pic/stagemap/11.png","0","","","0","0",},
    [2459] = {2702,3,"谁说我不是道士？我可是五斗米教两月速成班毕业的优秀学员，专精呼风唤雨方向。",11063,1,0,0,0,0,0,11063,0,31,1,"pic/stagemap/11.png","0","","","0","0",},
    [2460] = {2702,4,"……",1,1,0,21,0,0,0,11063,0,0,1,"pic/stagemap/11.png","0","","","0","0",},
    [2461] = {2703,1,"本以为亮亮你招风只是随口说说，没想到真将风招来了。",1,1,0,30,0,0,0,19005,0,0,1,"pic/stagemap/11.png","0","","","0","0",},
    [2462] = {2703,2,"快看，黄盖已带着小船朝曹操水军开去。那小船装满火油，只等点火……",19005,1,0,0,0,0,0,19005,0,26,1,"pic/stagemap/11.png","0","","","0","0",},
