    [2806] = {2842,1,"中山靖王之后刘玄德拜见吴国太。",11773,11951,0,0,0,0,0,11773,0,0,1,"pic/stagemap/5.png","0","","","0","0",},
    [2807] = {2842,2,"没想到刘备长得竟是如此英俊潇洒，气概不凡，不错不错，可以配得上我女儿。",11951,11951,0,30,0,0,0,11773,0,0,1,"pic/stagemap/5.png","0","","","0","0",},
    [2808] = {2842,3,"玄德龙行虎步龙凤之姿，仁德之名更是遍天下，国太得此佳婿，真是可喜可贺。",11941,11941,0,35,0,0,0,11773,0,0,1,"pic/stagemap/5.png","0","","","0","0",},
    [2809] = {2842,4,"咦，你身边这位这个粉妆玉砌的少年是？",11951,11951,0,0,0,0,0,11773,0,0,1,"pic/stagemap/5.png","0","","","0","0",},
    [2810] = {2842,5,"此乃我知交好友，多次救我性命，这次是特意陪我来东吴一起迎亲的。",11773,11951,0,0,0,0,0,11773,0,0,1,"pic/stagemap/5.png","0","","","0","0",},
    [2811] = {2842,6,"不错不错，真是个英俊的少年将军，来人，赐酒。",11951,11951,0,4,0,0,0,11773,0,0,1,"pic/stagemap/5.png","0","","","0","0",},
    [2812] = {2843,1,"（小声地）刘皇叔，我刚才在周围观察，发现走廊四周埋伏了刀斧手。",1,1,0,17,0,0,0,11773,0,0,1,"0","0","","","0","0",},
