    [2814] = {2843,3,"不要紧张，你可向吴国太哭诉，她对你如此满意，一定会出手救你。",1,1,0,45,0,0,0,11773,0,0,1,"0","0","","","0","0",},
    [2815] = {2844,1,"（大哭）国太要杀刘备，直接动手便是，又何必在此宴请我。",11773,11773,0,15,0,0,0,11951,0,0,1,"pic/stagemap/5.png","0","","","0","0",},
    [2816] = {2844,2,"这是哪里的话，你既为我女婿，我又怎么能杀你。",11951,11773,0,0,0,0,0,11951,0,18,1,"pic/stagemap/5.png","0","","","0","0",},
    [2817] = {2844,3,"既然如此，您为何在走廊埋伏刀斧手，那不就是用来杀我的吗？",11773,11773,0,13,0,0,0,11951,0,0,1,"pic/stagemap/5.png","0","","","0","0",},
    [2818] = {2844,4,"仲谋，是不是你干的！！",11951,11773,0,0,0,0,0,11951,0,29,1,"pic/stagemap/5.png","0","","","0","0",},
    [2819] = {2844,5,"咳……母亲，这事真不是我干的，对了，一定是暗恋我妹的某些人做出的过激行为。",11660,11773,0,0,0,0,0,11660,0,21,1,"pic/stagemap/5.png","0","","","0","0",},
    [2820] = {2845,1,"吴夫人，江东之地太多人想杀我，我不能久留了。",11773,11773,0,43,0,0,0,11951,0,0,1,"0","0","","","0","0",},
    [2821] = {2845,2,"放心吧，你今夜住到内府来，我到要看看谁敢冲到我这里杀人。",11951,11773,0,0,0,0,0,11951,0,19,1,"0","0","","","0","0",},
