    [2826] = {2851,1,"孙权好狡猾，自刘备结婚后，就给他修房子、不断送珠宝，侍女，这是要腐化刘备啊。",19005,1,0,0,0,0,0,19005,0,19,1,"pic/stagemap/5.png","0","","","0","0",},
    [2827] = {2851,2,"刘备迷恋声色，不想再回荆州，这可不行，得想个办法让他回去才行。",1,1,0,48,0,0,0,19005,0,0,1,"pic/stagemap/5.png","0","","","0","0",},
