    [2829] = {2851,4,"你什么时候把锦囊拆了…算了，先按照纸条上的主意把刘备骗回去再说。",1,1,0,21,0,0,0,19003,0,0,1,"pic/stagemap/5.png","0","","","0","0",},
    [2830] = {2852,1,"刘皇叔，大事不好，军师派人来报，说曹操发兵五十万进攻荆州。你得赶紧回去！",1,1,0,26,0,0,0,11975,0,0,1,"0","0","","","0","0",},
