    [2832] = {2852,3,"不用说了，我听到了，俗话说嫁鸡随鸡，既然荆州有危机，那我便和你一起回去。",11975,11773,0,0,0,0,0,11975,0,0,1,"0","0","","","0","0",},
    [2833] = {2852,4,"唉，就怕国太和仲谋不肯放我走啊。",11773,11773,0,43,0,0,0,11975,0,0,1,"0","0","","","0","0",},
    [2834] = {2852,5,"不用怕，元旦那天，我们就说去江边祭祖，然后趁机溜走！",11975,11773,0,0,0,0,0,11975,0,45,1,"0","0","","","0","0",},
