local m = { 
    __index = function(t, k) 
        if k == "toObject" then
            return function()  
                local o = {}
                for key, v in pairs (__key_map) do 
                    o[key] = t._raw[v]
                end
                return o
            end 
        end
        
        assert(__key_map[k], "cannot find " .. k .. " in record_story_dialogue")
        
        
        return t._raw[__key_map[k]]
    end
}


function story_dialogue.getLength()
    return #story_dialogue._data
end



function story_dialogue.hasKey(k)
  if __key_map[k] == nil then
    return false
  else
    return true
  end
end


---
--@return @class record_story_dialogue
function story_dialogue.indexOf(index)
    if index == nil then
        return nil
    end
    
    return setmetatable({_raw = story_dialogue._data[index]}, m)
    
end

---
--@return @class record_story_dialogue
function story_dialogue.get(story_id,step)
    
    local k = story_id .. '_' .. step
    return story_dialogue.indexOf(__index_story_id_step[k])
        
end



function story_dialogue.set(story_id,step, key, value)
    local record = story_dialogue.get(story_id,step)
    if record then
        local keyIndex = __key_map[key]
        if keyIndex then
            record._raw[keyIndex] = value
        end
    end
end




function story_dialogue.get_index_data()
    return __index_story_id_step
end