--[[	检查是否有英雄	actor_id:要检查的英雄id	skin_id:要检查的皮肤id--]]local QSBAction = import(".QSBAction")local QSBArgsHasActor = class("QSBArgsHasActor", QSBAction)function QSBArgsHasActor:_execute(dt)	local actor_id = self._options.actor_id	local skin_id = self._options.skin_id	if actor_id == nil then		self:finished({select = false})		return	end	local targets	if self._options.teammate then		targets = app.battle:getMyTeammates(self._attacker,			self._options.includeSelf, self._options.justHero)	end	if targets == nil then		self:finished({select = false})		return	end	for i,hero in ipairs(targets) do		if hero:getActorID() == actor_id then			if skin_id then				self:finished({select = (skin_id == hero:getSkinInfo().skins_id)})			else				self:finished({select = true})			end			return		end	end	self:finished({select = false})endreturn QSBArgsHasActor