	[1008] = {difficulty = 1, groupId = 1, taskId = 1008, monsterId = 301008, clueType = 0, clueDescription = '<c=blue>無關鍵資訊</c>', memberDescription = '廣興錢莊的大掌櫃。\n平日對居民們的態度十分傲慢，最近更是又新招了兩名打手。在南林湖村已然是僅次于村長的存在了。', angleAlive = 300, angleDead = 90},
	[1009] = {difficulty = 1, groupId = 2, taskId = 1009, monsterId = 301009, clueType = 1, clueDescription = '<c=blue>可獲取關鍵資訊</c>', memberDescription = '來三邪關遊歷的三個古怪法師之一。\n為人急躁，喜好濫殺無辜。似乎受了很重的傷，一定與某人發生過激烈的爭鬥。', angleAlive = 300, angleDead = 90},
	[1010] = {difficulty = 1, groupId = 2, taskId = 1010, monsterId = 301010, clueType = 2, clueDescription = '<c=blue>可獲取關鍵資訊</c>', memberDescription = '虎威鏢局的總鏢頭，綽號鎮三邪。\n本應與手下兄弟情同手足的他，最近卻和周九申大打出手。', angleAlive = 300, angleDead = 90},
	[1011] = {difficulty = 1, groupId = 2, taskId = 1011, monsterId = 301011, clueType = 3, clueDescription = '<c=blue>可獲取關鍵資訊</c>', memberDescription = '三邪關一帶有名的鏢師。\n最近好像與其掛靠的鏢局鏢頭有些矛盾。似乎跟某個貴重物品有關。', angleAlive = 300, angleDead = 90},
	[1012] = {difficulty = 1, groupId = 2, taskId = 1012, monsterId = 301012, clueType = 0, clueDescription = '<c=blue>無關鍵資訊</c>', memberDescription = '來三邪關遊歷的三個古怪法師之一。\n整天遊蕩在街上，但也沒有特別引入注意的地方。很少與旁人搭話。', angleAlive = 300, angleDead = 90},
