	[1014] = {difficulty = 1, groupId = 2, taskId = 1014, monsterId = 301014, clueType = 0, clueDescription = '<c=blue>無關鍵資訊</c>', memberDescription = '剛剛來到此地的奇怪旅人。\n追尋著已死武僧的足跡，似乎也對那盞金缽充滿了興趣。', angleAlive = 300, angleDead = 90},
