	[1016] = {difficulty = 1, groupId = 2, taskId = 1016, monsterId = 301016, clueType = 5, clueDescription = '<c=blue>可獲取關鍵資訊</c>', memberDescription = '此人似乎一路尾隨武僧來到此地。\n十分執著的尋找有關金缽的線索。他似乎知道有關金缽的秘密。如果說武僧發生了什麼，他必定知道點東西。', angleAlive = 300, angleDead = 90},
