	[2024] = {difficulty = 2, groupId = 3, taskId = 2024, monsterId = 302024, clueType = 0, clueDescription = '<c=blue>無關鍵資訊</c>', memberDescription = '君子劍三兄弟中的小師弟。\n個性內向不喜言語，與二師兄齊岳關係最為不錯。齊嶽死后，他很傷心，言語間盡是對自己的埋怨。', angleAlive = 300, angleDead = 90},
	[2025] = {difficulty = 2, groupId = 4, taskId = 2025, monsterId = 302025, clueType = 1, clueDescription = '<c=blue>可獲取關鍵資訊</c>', memberDescription = '虎穴聯成員。\n平日裡都是規規矩矩，近些時候，卻被人發現有時候會鬼鬼祟祟的出現在不該出現的地方，並且與某人會面。', angleAlive = 300, angleDead = 90},
	[2026] = {difficulty = 2, groupId = 4, taskId = 2026, monsterId = 302026, clueType = 2, clueDescription = '<c=blue>可獲取關鍵資訊</c>', memberDescription = '虎峽谷一帶的土匪。\n以往他只劫財不殺人，近日卻突然幹起了殺人的勾當。搞得村裡民不聊生。', angleAlive = 300, angleDead = 90},
	[2027] = {difficulty = 2, groupId = 4, taskId = 2027, monsterId = 302027, clueType = 3, clueDescription = '<c=blue>可獲取關鍵資訊</c>', memberDescription = '生活與虎峽谷的普通村民。\n天性懦弱，生活處境一般。不知道從哪天開始，完全像變了個人，對其他村民更是指手畫腳。', angleAlive = 300, angleDead = 90},
	[2028] = {difficulty = 2, groupId = 4, taskId = 2028, monsterId = 302028, clueType = 4, clueDescription = '<c=blue>可獲取關鍵資訊</c>', memberDescription = '幻影門成員。\n在門中不被人看重，但又渴望得到重用，嫉妒心極強，最近一些日子頻繁在門裡與沼澤之間來往。', angleAlive = 300, angleDead = 90},
	[2029] = {difficulty = 2, groupId = 4, taskId = 2029, monsterId = 302029, clueType = 5, clueDescription = '<c=blue>可獲取關鍵資訊</c>', memberDescription = '不知何時出現在虎峽谷內的女子。\n夜晚時分，有人在河邊看到匆匆離去的人影，很像此女子。走近才發現，河裡飄著屍體，令人毛骨悚然。', angleAlive = 300, angleDead = 90},
	[2030] = {difficulty = 2, groupId = 4, taskId = 2030, monsterId = 302030, clueType = 0, clueDescription = '<c=blue>無關鍵資訊</c>', memberDescription = '虎峽谷附近的地痞。\n天性膽小，經常在夜裡幹著小偷小摸的事情。時常出沒于虎穴聯附近的小屋附近。', angleAlive = 300, angleDead = 90},
	[2031] = {difficulty = 2, groupId = 4, taskId = 2031, monsterId = 302031, clueType = 0, clueDescription = '<c=blue>無關鍵資訊</c>', memberDescription = '脾氣有些怪異的流浪者。\n似乎是從別處潛逃至虎峽谷不久，並且在到處打聽虎穴聯和幻影門的事情。', angleAlive = 300, angleDead = 90},
	[2032] = {difficulty = 2, groupId = 4, taskId = 2032, monsterId = 302032, clueType = 0, clueDescription = '<c=blue>無關鍵資訊</c>', memberDescription = '經常遊蕩在虎峽谷沼澤的外鄉人。\n而且多半是在夜晚，但從來沒有人見到其他人與他在一起，永遠只有他一個人。', angleAlive = 300, angleDead = 90},
	[3001] = {difficulty = 3, groupId = 1, taskId = 3001, monsterId = 303001, clueType = 1, clueDescription = '<c=blue>可獲取關鍵資訊</c>', memberDescription = '南林湖村民。\n遊手好閒，小偷小摸為生，鄰里厭棄。近日被人看到似乎偷得什麼寶貝。', angleAlive = 300, angleDead = 90},
	[3002] = {difficulty = 3, groupId = 1, taskId = 3002, monsterId = 303002, clueType = 2, clueDescription = '<c=blue>可獲取關鍵資訊</c>', memberDescription = '廣興錢莊打手之一。\n聽說以前是做響馬的馬賊，平時仗著錢一枚的勢力為鄉里所患。近日被人看到與賣餅師傅發生爭鬥。', angleAlive = 300, angleDead = 90},
