	[4006] = {difficulty = 4, groupId = 1, taskId = 4006, monsterId = 304006, clueType = 0, clueDescription = '<c=blue>無關鍵資訊</c>', memberDescription = '村裡賣餅人收的徒弟。\n為人內向，話不多。經常替他師父挑擔賣餅。', angleAlive = 300, angleDead = 90},
	[4007] = {difficulty = 4, groupId = 1, taskId = 4007, monsterId = 304007, clueType = 0, clueDescription = '<c=blue>無關鍵資訊</c>', memberDescription = '5年前帶著徒弟來南林湖村定居的外鄉人，現在以賣餅為生。\n他的過去成謎，似乎也是武林中人。', angleAlive = 300, angleDead = 90},
	[4008] = {difficulty = 4, groupId = 1, taskId = 4008, monsterId = 304008, clueType = 0, clueDescription = '<c=blue>無關鍵資訊</c>', memberDescription = '廣興錢莊的大掌櫃。\n平日對居民們的態度十分傲慢，最近更是又新招了兩名打手。在南林湖村已然是僅次于村長的存在了。', angleAlive = 300, angleDead = 90},
	[4009] = {difficulty = 4, groupId = 2, taskId = 4009, monsterId = 304009, clueType = 1, clueDescription = '<c=blue>可獲取關鍵資訊</c>', memberDescription = '來三邪關遊歷的三個古怪法師之一。\n為人急躁，喜好濫殺無辜。似乎受了很重的傷，一定與某人發生過激烈的爭鬥。', angleAlive = 300, angleDead = 90},
	[4010] = {difficulty = 4, groupId = 2, taskId = 4010, monsterId = 304010, clueType = 2, clueDescription = '<c=blue>可獲取關鍵資訊</c>', memberDescription = '虎威鏢局的總鏢頭，綽號鎮三邪。\n本應與手下兄弟情同手足的他，最近卻和周九申大打出手。', angleAlive = 300, angleDead = 90},
	[4011] = {difficulty = 4, groupId = 2, taskId = 4011, monsterId = 304011, clueType = 3, clueDescription = '<c=blue>可獲取關鍵資訊</c>', memberDescription = '三邪關一帶有名的鏢師。\n最近好像與其掛靠的鏢局鏢頭有些矛盾。似乎跟某個貴重物品有關。', angleAlive = 300, angleDead = 90},
	[4012] = {difficulty = 4, groupId = 2, taskId = 4012, monsterId = 304012, clueType = 0, clueDescription = '<c=blue>無關鍵資訊</c>', memberDescription = '來三邪關遊歷的三個古怪法師之一。\n整天遊蕩在街上，但也沒有特別引入注意的地方。很少與旁人搭話。', angleAlive = 300, angleDead = 90},
	[4013] = {difficulty = 4, groupId = 2, taskId = 4013, monsterId = 304013, clueType = 0, clueDescription = '<c=blue>無關鍵資訊</c>', memberDescription = '來三邪關遊歷的三個古怪法師之一。\n在受傷的法師之后，來到此地，有人看到他為其療傷。', angleAlive = 300, angleDead = 90},
	[4014] = {difficulty = 4, groupId = 2, taskId = 4014, monsterId = 304014, clueType = 0, clueDescription = '<c=blue>無關鍵資訊</c>', memberDescription = '剛剛來到此地的奇怪旅人。\n追尋著已死武僧的足跡，似乎也對那盞金缽充滿了興趣。', angleAlive = 300, angleDead = 90},
	[4015] = {difficulty = 4, groupId = 2, taskId = 4015, monsterId = 304015, clueType = 4, clueDescription = '<c=blue>可獲取關鍵資訊</c>', memberDescription = '生活在三邪關的鐵匠。\n此人同時也是一名賞金獵人。最近和虎威鏢局的人走得很近，不知道在謀劃著什麼。', angleAlive = 300, angleDead = 90},
	[4016] = {difficulty = 4, groupId = 2, taskId = 4016, monsterId = 304016, clueType = 5, clueDescription = '<c=blue>可獲取關鍵資訊</c>', memberDescription = '此人似乎一路尾隨武僧來到此地。\n十分執著的尋找有關金缽的線索。他似乎知道有關金缽的秘密。如果說武僧發生了什麼，他必定知道點東西。', angleAlive = 300, angleDead = 90},
	[4017] = {difficulty = 4, groupId = 3, taskId = 4017, monsterId = 304017, clueType = 1, clueDescription = '<c=blue>可獲取關鍵資訊</c>', memberDescription = '收到君子三劍中齊嶽的邀請，前去花亭平原煮酒切磋的劍客。\n齊嶽被暗殺的當天，還與他、許堅和其他兩名君子劍師兄弟喝酒。', angleAlive = 300, angleDead = 90},
	[4018] = {difficulty = 4, groupId = 3, taskId = 4018, monsterId = 304018, clueType = 2, clueDescription = '<c=blue>可獲取關鍵資訊</c>', memberDescription = '曾被君子三劍中的齊嶽打敗的大盜。\n為人其軟怕硬、對齊岳懷恨在心。在被齊嶽送往衙門的途中似乎聽到什麼不該聽到的東西。', angleAlive = 300, angleDead = 90},
	[4019] = {difficulty = 4, groupId = 3, taskId = 4019, monsterId = 304019, clueType = 3, clueDescription = '<c=blue>可獲取關鍵資訊</c>', memberDescription = '收到君子三劍中齊嶽的邀請，前去花亭平原煮酒切磋的劍客。\n個性粗狂，曾經與齊嶽有過衝突，算是不打不成交的兄弟。齊岳死前曾被看到經過齊嶽住處。', angleAlive = 300, angleDead = 90},
	[4020] = {difficulty = 4, groupId = 3, taskId = 4020, monsterId = 304020, clueType = 4, clueDescription = '<c=blue>可獲取關鍵資訊</c>', memberDescription = '曾被君子三劍中的齊嶽打敗的採花盜賊。\n為人睚眥必報、心狠手辣。齊岳死時，他也在花亭平原附近出沒。', angleAlive = 300, angleDead = 90},
	[4021] = {difficulty = 4, groupId = 3, taskId = 4021, monsterId = 304021, clueType = 0, clueDescription = '<c=blue>無關鍵資訊</c>', memberDescription = '收到君子三劍中齊嶽的邀請，前去花亭平原煮酒切磋的劍客。\n與君子三劍中的廣成師兄最為交好。', angleAlive = 300, angleDead = 90},
	[4022] = {difficulty = 4, groupId = 3, taskId = 4022, monsterId = 304022, clueType = 0, clueDescription = '<c=blue>無關鍵資訊</c>', memberDescription = '君子三劍中的齊岳和長陽的大師兄。\n為人豪爽、不拘小節。與兩名師弟的關係都不錯，平時喜歡喝酒。', angleAlive = 300, angleDead = 90},
	[4023] = {difficulty = 4, groupId = 3, taskId = 4023, monsterId = 304023, clueType = 5, clueDescription = '<c=blue>可獲取關鍵資訊</c>', memberDescription = '花亭平原本地的制毒師。\n平日與君子三劍沒有任何關係，但在聽說了齊嶽死后，卻意外的有些不安。', angleAlive = 300, angleDead = 90},
	[4024] = {difficulty = 4, groupId = 3, taskId = 4024, monsterId = 304024, clueType = 0, clueDescription = '<c=blue>無關鍵資訊</c>', memberDescription = '君子劍三兄弟中的小師弟。\n個性內向不喜言語，與二師兄齊岳關係最為不錯。齊嶽死后，他很傷心，言語間盡是對自己的埋怨。', angleAlive = 300, angleDead = 90},
	[4025] = {difficulty = 4, groupId = 4, taskId = 4025, monsterId = 304025, clueType = 1, clueDescription = '<c=blue>可獲取關鍵資訊</c>', memberDescription = '虎穴聯成員。\n平日裡都是規規矩矩，近些時候，卻被人發現有時候會鬼鬼祟祟的出現在不該出現的地方，並且與某人會面。', angleAlive = 300, angleDead = 90},
	[4026] = {difficulty = 4, groupId = 4, taskId = 4026, monsterId = 304026, clueType = 2, clueDescription = '<c=blue>可獲取關鍵資訊</c>', memberDescription = '虎峽谷一帶的土匪。\n以往他只劫財不殺人，近日卻突然幹起了殺人的勾當。搞得村裡民不聊生。', angleAlive = 300, angleDead = 90},
	[4027] = {difficulty = 4, groupId = 4, taskId = 4027, monsterId = 304027, clueType = 3, clueDescription = '<c=blue>可獲取關鍵資訊</c>', memberDescription = '生活與虎峽谷的普通村民。\n天性懦弱，生活處境一般。不知道從哪天開始，完全像變了個人，對其他村民更是指手畫腳。', angleAlive = 300, angleDead = 90},
	[4028] = {difficulty = 4, groupId = 4, taskId = 4028, monsterId = 304028, clueType = 4, clueDescription = '<c=blue>可獲取關鍵資訊</c>', memberDescription = '幻影門成員。\n在門中不被人看重，但又渴望得到重用，嫉妒心極強，最近一些日子頻繁在門裡與沼澤之間來往。', angleAlive = 300, angleDead = 90},
	[4029] = {difficulty = 4, groupId = 4, taskId = 4029, monsterId = 304029, clueType = 5, clueDescription = '<c=blue>可獲取關鍵資訊</c>', memberDescription = '不知何時出現在虎峽谷內的女子。\n夜晚時分，有人在河邊看到匆匆離去的人影，很像此女子。走近才發現，河裡飄著屍體，令人毛骨悚然。', angleAlive = 300, angleDead = 90},
	[4030] = {difficulty = 4, groupId = 4, taskId = 4030, monsterId = 304030, clueType = 0, clueDescription = '<c=blue>無關鍵資訊</c>', memberDescription = '虎峽谷附近的地痞。\n天性膽小，經常在夜裡幹著小偷小摸的事情。時常出沒于虎穴聯附近的小屋附近。', angleAlive = 300, angleDead = 90},
	[4031] = {difficulty = 4, groupId = 4, taskId = 4031, monsterId = 304031, clueType = 0, clueDescription = '<c=blue>無關鍵資訊</c>', memberDescription = '脾氣有些怪異的流浪者。\n似乎是從別處潛逃至虎峽谷不久，並且在到處打聽虎穴聯和幻影門的事情。', angleAlive = 300, angleDead = 90},
	[4032] = {difficulty = 4, groupId = 4, taskId = 4032, monsterId = 304032, clueType = 0, clueDescription = '<c=blue>無關鍵資訊</c>', memberDescription = '經常遊蕩在虎峽谷沼澤的外鄉人。\n而且多半是在夜晚，但從來沒有人見到其他人與他在一起，永遠只有他一個人。', angleAlive = 300, angleDead = 90},
