	[5024] = {difficulty = 5, groupId = 3, taskId = 5024, monsterId = 305024, clueType = 0, clueDescription = '<c=blue>無關鍵資訊</c>', memberDescription = '君子劍三兄弟中的小師弟。\n個性內向不喜言語，與二師兄齊岳關係最為不錯。齊嶽死后，他很傷心，言語間盡是對自己的埋怨。', angleAlive = 300, angleDead = 90},
	[5025] = {difficulty = 5, groupId = 4, taskId = 5025, monsterId = 305025, clueType = 1, clueDescription = '<c=blue>可獲取關鍵資訊</c>', memberDescription = '虎穴聯成員。\n平日裡都是規規矩矩，近些時候，卻被人發現有時候會鬼鬼祟祟的出現在不該出現的地方，並且與某人會面。', angleAlive = 300, angleDead = 90},
	[5026] = {difficulty = 5, groupId = 4, taskId = 5026, monsterId = 305026, clueType = 2, clueDescription = '<c=blue>可獲取關鍵資訊</c>', memberDescription = '虎峽谷一帶的土匪。\n以往他只劫財不殺人，近日卻突然幹起了殺人的勾當。搞得村裡民不聊生。', angleAlive = 300, angleDead = 90},
	[5027] = {difficulty = 5, groupId = 4, taskId = 5027, monsterId = 305027, clueType = 3, clueDescription = '<c=blue>可獲取關鍵資訊</c>', memberDescription = '生活與虎峽谷的普通村民。\n天性懦弱，生活處境一般。不知道從哪天開始，完全像變了個人，對其他村民更是指手畫腳。', angleAlive = 300, angleDead = 90},
	[5028] = {difficulty = 5, groupId = 4, taskId = 5028, monsterId = 305028, clueType = 4, clueDescription = '<c=blue>可獲取關鍵資訊</c>', memberDescription = '幻影門成員。\n在門中不被人看重，但又渴望得到重用，嫉妒心極強，最近一些日子頻繁在門裡與沼澤之間來往。', angleAlive = 300, angleDead = 90},
	[5029] = {difficulty = 5, groupId = 4, taskId = 5029, monsterId = 305029, clueType = 5, clueDescription = '<c=blue>可獲取關鍵資訊</c>', memberDescription = '不知何時出現在虎峽谷內的女子。\n夜晚時分，有人在河邊看到匆匆離去的人影，很像此女子。走近才發現，河裡飄著屍體，令人毛骨悚然。', angleAlive = 300, angleDead = 90},
	[5030] = {difficulty = 5, groupId = 4, taskId = 5030, monsterId = 305030, clueType = 0, clueDescription = '<c=blue>無關鍵資訊</c>', memberDescription = '虎峽谷附近的地痞。\n天性膽小，經常在夜裡幹著小偷小摸的事情。時常出沒于虎穴聯附近的小屋附近。', angleAlive = 300, angleDead = 90},
	[5031] = {difficulty = 5, groupId = 4, taskId = 5031, monsterId = 305031, clueType = 0, clueDescription = '<c=blue>無關鍵資訊</c>', memberDescription = '脾氣有些怪異的流浪者。\n似乎是從別處潛逃至虎峽谷不久，並且在到處打聽虎穴聯和幻影門的事情。', angleAlive = 300, angleDead = 90},
	[5032] = {difficulty = 5, groupId = 4, taskId = 5032, monsterId = 305032, clueType = 0, clueDescription = '<c=blue>無關鍵資訊</c>', memberDescription = '經常遊蕩在虎峽谷沼澤的外鄉人。\n而且多半是在夜晚，但從來沒有人見到其他人與他在一起，永遠只有他一個人。', angleAlive = 300, angleDead = 90},
};
i3k_db_knightly_detective_ringleader = 
{
	[1001] = {difficulty = 1, groupId = 1, taskId = 10001, monsterId = 311001, description = {{detail = '張祿交代說他在一次行竊途中，看到窗內大盜的剪影后，嚇得翻牆而逃了。只隱約聽到一句“放心，那傢伙很有錢，是個財主”', brief = '大盜的目標是財主'}, {detail = '賈懷丹曾見過村裡的賣餅師父和其徒弟幾番出沒于采心大盜的作案現場。但並有看到過程。', brief = '大盜可能與賣餅人有關'}, {detail = '真俄仁曾接待過江湖術士，並且跟他是老相識。江湖術士要求他看好掌櫃的行蹤，但並沒有說明目的。', brief = '江湖術士可能知道大盜真容'}, {detail = '他與錢大掌櫃找來的打手之一非常交好，與那神秘江湖術士似乎也多有聯繫，這三人曾一起見過面。並講出大盜正是江湖術士找來的。', brief = '大盜與江湖術士合謀'}, {detail = '雖然老頭極力否認自己與大盜的關係，還是從他的身上搜到了一封密信，上面寫著“月掛中天，武院再見”八個字', brief = '大盜仍在南林湖'}, }, ringleaderDesc = '賣餅人穀亦糧曾經的師弟，真正的采心大盜。\n受雇于江湖術士之托，在南林湖採集12份心頭血。江湖術士與錢兩貫合作后，由真俄仁潛入錢家為金拙尋找機會，除掉錢一枚。妄圖一石二鳥，既能集齊心頭血，又可輔助錢兩貫上位，得到兩份傭金。', story = '南林湖最近發生了11起凶案，令村民們人心惶惶，更令人恐懼的是每具屍首都被挖去了心臟，人們都稱兇手為采心大盜，但卻沒人知道此人到底是誰，也不清楚此人的目的！', angleAlive = 90, angleDead = 90},
	[1002] = {difficulty = 1, groupId = 2, taskId = 10002, monsterId = 311002, description = {{detail = '法師交代說，在與武僧纏鬥的過程中，發現一個黑影也莫名出現在附近，並遲遲沒有離去。不過看體型應該是個男子。', brief = '黑影是個男子'}, {detail = '他與鏢師在路上發現死去的武僧並從其身上得到金缽。在一番爭執后，二人決定將金缽送往鐵匠處燒融。', brief = '金缽在鐵匠手中'}, {detail = '于登峰在將金缽交于鐵匠之后，剛準備離去，卻無意間聽到門內鐵匠正與某人交談，隱約聽到聲音說“看他們並不知道此中秘密”', brief = '鐵匠與他人有勾結'}, {detail = '雖然鐵匠極力否認金缽與自己有關，但最后還是在他身上找到一張字條，“帶到城內給我”', brief = '黑影在三邪關城內'}, {detail = '據他所說，金缽內藏著絕密武功秘笈，並且提到有一個賞金獵人也知道此事。', brief = '黑影是賞金獵人'}, }, ringleaderDesc = '墮落的賞金獵人，刀大寶的上家。\n很早之前就知道金缽內藏有武功心法。在武僧被打傷后，他便已經盯上了其手中的金缽。不巧，重傷不治武僧和金缽一同被虎威鏢局的人發現。更讓他意想不到的是，虎威鏢局的鏢師並不知道金缽的秘密，反而將金缽交給了自己的手下，偽裝成了鐵匠的肖大寶。', story = '傳聞浮游寺的金缽不見了蹤跡，而與此同時，三邪關附近出現了一具武僧的屍體，這二者之間是否存在著某種聯繫，還不得而知。但可以知道的是三邪關附近似乎也突然出現了不少怪人。', angleAlive = 90, angleDead = 90},
	[1003] = {difficulty = 1, groupId = 3, taskId = 10003, monsterId = 311003, description = {{detail = '呂符在酒后隱約看到有人在齊嶽杯中下毒，但並沒有看清其樣貌。只感覺可能是君子劍其中一人。', brief = '兇手在君子劍之中'}, {detail = '他告訴你，他曾在一次偷竊的過程中被齊岳與其師弟發現。兩人在扭送他去衙門的時候，曾談到有關其大師兄私藏劍譜的事。', brief = '兇手的目標是劍譜'}, {detail = '在齊嶽死前的那個晚上，他曾目睹有人鬼鬼祟祟的進入齊嶽的房間。似乎是本地的一個採花賊，沒過多久卻又倉皇逃竄。', brief = '齊岳房中可能有秘密'}, {detail = '申白馬承認他想報復齊嶽，但在他潛入齊嶽屋內之后，卻聽到齊嶽小聲說道“這次你死定了”。嚇得他轉身就溜了。', brief = '齊嶽要對某人不利？'}, {detail = '他向你坦白，齊嶽這次舉行的煮酒切磋只是個幌子。真實的目的可能是在過程中殺死自己的大師兄廣成。並從自己手裡買了毒藥和解藥。', brief = '齊嶽買了毒藥和解藥'}, }, ringleaderDesc = '君子三劍之一。\n對師父將君子劍劍譜交給大師兄廣成十分不滿。本想聯合三師弟搶奪劍譜，無奈三師弟不想這麼做，只能鋌而走險，組織煮酒切磋，先詐死，而后暗殺廣成。可惜計畫只實行了一半便有人來攪局。但無論結果如何，自己都已經不再是君子劍了。', story = '君子三劍邀約各路俠士齊聚花亭平原，煮酒論劍，本應是一次盡興的江湖切磋，不料君子劍之一的齊岳卻死于酒會之上。而更奇怪的是，齊嶽的屍體突然消失。這背后到底隱藏著什麼秘密，又是誰殺了齊嶽。', angleAlive = 90, angleDead = 90},
	[1004] = {difficulty = 1, groupId = 4, taskId = 10004, monsterId = 311004, description = {{detail = '殷北提到與他碰面的是個女子，但其並不像主腦，因為每次會面時似乎遠處還有人在暗處注視。', brief = '“仇鬼”與一名女子有關'}, {detail = '邢元良交代有人以給他大量的黃金為前提，讓他在虎峽谷製造混亂，但並未說明目的。', brief = '有人希望製造混亂'}, {detail = '從候俊才身上搜到的字條“只要你幫我在村裡散播謠言，就少不了你的好處”。', brief = '有人希望散佈謠言'}, {detail = '據向永長所說，“仇鬼”希望借助幻影門的力量來剿滅虎峽聯，並在成功后將功勞全算在自己頭上。', brief = '“仇鬼”對虎峽聯充滿仇恨'}, {detail = '原來古馨正是“仇鬼”的妹妹，同樣經歷了慘痛過去的她，在暗地裡協助自己的哥哥。', brief = '“仇鬼”是古馨的哥哥'}, }, ringleaderDesc = '心靈扭曲的復仇者。\n在很小的時候，父母便在虎峽聯的一次行動中意外喪命。\n從此他便只有一個目的：復仇。他希望在村裡散播謠言並製造混亂借此擾亂虎峽聯，並收買虎峽聯成員以便獲取內部消息，最后再聯手幻影門覆滅虎峽聯。', story = '虎穴聯成員近日經常在外出調查村民死亡事件時被人暗殺，而村民們之間流傳著一個關于復仇的故事，更暗地裡稱復仇者為“仇鬼”。這些言語傳到虎穴聯，更是鬧得虎穴聯內部人心惶惶。', angleAlive = 90, angleDead = 90},
	[2001] = {difficulty = 2, groupId = 1, taskId = 20001, monsterId = 312001, description = {{detail = '張祿交代說他在一次行竊途中，看到窗內大盜的剪影后，嚇得翻牆而逃了。只隱約聽到一句“放心，那傢伙很有錢，是個財主”', brief = '大盜的目標是財主'}, {detail = '賈懷丹曾見過村裡的賣餅師父和其徒弟幾番出沒于采心大盜的作案現場。但並有看到過程。', brief = '大盜可能與賣餅人有關'}, {detail = '真俄仁曾接待過江湖術士，並且跟他是老相識。江湖術士要求他看好掌櫃的行蹤，但並沒有說明目的。', brief = '江湖術士可能知道大盜真容'}, {detail = '他與錢大掌櫃找來的打手之一非常交好，與那神秘江湖術士似乎也多有聯繫，這三人曾一起見過面。並講出大盜正是江湖術士找來的。', brief = '大盜與江湖術士合謀'}, {detail = '雖然老頭極力否認自己與大盜的關係，還是從他的身上搜到了一封密信，上面寫著“月掛中天，武院再見”八個字', brief = '大盜仍在南林湖'}, }, ringleaderDesc = '賣餅人穀亦糧曾經的師弟，真正的采心大盜。\n受雇于江湖術士之托，在南林湖採集12份心頭血。江湖術士與錢兩貫合作后，由真俄仁潛入錢家為金拙尋找機會，除掉錢一枚。妄圖一石二鳥，既能集齊心頭血，又可輔助錢兩貫上位，得到兩份傭金。', story = '南林湖最近發生了11起凶案，令村民們人心惶惶，更令人恐懼的是每具屍首都被挖去了心臟，人們都稱兇手為采心大盜，但卻沒人知道此人到底是誰，也不清楚此人的目的！', angleAlive = 90, angleDead = 90},
	[2002] = {difficulty = 2, groupId = 2, taskId = 20002, monsterId = 312002, description = {{detail = '法師交代說，在與武僧纏鬥的過程中，發現一個黑影也莫名出現在附近，並遲遲沒有離去。不過看體型應該是個男子。', brief = '黑影是個男子'}, {detail = '他與鏢師在路上發現死去的武僧並從其身上得到金缽。在一番爭執后，二人決定將金缽送往鐵匠處燒融。', brief = '金缽在鐵匠手中'}, {detail = '于登峰在將金缽交于鐵匠之后，剛準備離去，卻無意間聽到門內鐵匠正與某人交談，隱約聽到聲音說“看他們並不知道此中秘密”', brief = '鐵匠與他人有勾結'}, {detail = '雖然鐵匠極力否認金缽與自己有關，但最后還是在他身上找到一張字條，“帶到城內給我”', brief = '黑影在三邪關城內'}, {detail = '據他所說，金缽內藏著絕密武功秘笈，並且提到有一個賞金獵人也知道此事。', brief = '黑影是賞金獵人'}, }, ringleaderDesc = '墮落的賞金獵人，刀大寶的上家。\n很早之前就知道金缽內藏有武功心法。在武僧被打傷后，他便已經盯上了其手中的金缽。不巧，重傷不治武僧和金缽一同被虎威鏢局的人發現。更讓他意想不到的是，虎威鏢局的鏢師並不知道金缽的秘密，反而將金缽交給了自己的手下，偽裝成了鐵匠的肖大寶。', story = '傳聞浮游寺的金缽不見了蹤跡，而與此同時，三邪關附近出現了一具武僧的屍體，這二者之間是否存在著某種聯繫，還不得而知。但可以知道的是三邪關附近似乎也突然出現了不少怪人。', angleAlive = 90, angleDead = 90},
	[2003] = {difficulty = 2, groupId = 3, taskId = 20003, monsterId = 312003, description = {{detail = '呂符在酒后隱約看到有人在齊嶽杯中下毒，但並沒有看清其樣貌。只感覺可能是君子劍其中一人。', brief = '兇手在君子劍之中'}, {detail = '他告訴你，他曾在一次偷竊的過程中被齊岳與其師弟發現。兩人在扭送他去衙門的時候，曾談到有關其大師兄私藏劍譜的事。', brief = '兇手的目標是劍譜'}, {detail = '在齊嶽死前的那個晚上，他曾目睹有人鬼鬼祟祟的進入齊嶽的房間。似乎是本地的一個採花賊，沒過多久卻又倉皇逃竄。', brief = '齊岳房中可能有秘密'}, {detail = '申白馬承認他想報復齊嶽，但在他潛入齊嶽屋內之后，卻聽到齊嶽小聲說道“這次你死定了”。嚇得他轉身就溜了。', brief = '齊嶽要對某人不利？'}, {detail = '他向你坦白，齊嶽這次舉行的煮酒切磋只是個幌子。真實的目的可能是在過程中殺死自己的大師兄廣成。並從自己手裡買了毒藥和解藥。', brief = '齊嶽買了毒藥和解藥'}, }, ringleaderDesc = '君子三劍之一。\n對師父將君子劍劍譜交給大師兄廣成十分不滿。本想聯合三師弟搶奪劍譜，無奈三師弟不想這麼做，只能鋌而走險，組織煮酒切磋，先詐死，而后暗殺廣成。可惜計畫只實行了一半便有人來攪局。但無論結果如何，自己都已經不再是君子劍了。', story = '君子三劍邀約各路俠士齊聚花亭平原，煮酒論劍，本應是一次盡興的江湖切磋，不料君子劍之一的齊岳卻死于酒會之上。而更奇怪的是，齊嶽的屍體突然消失。這背后到底隱藏著什麼秘密，又是誰殺了齊嶽。', angleAlive = 90, angleDead = 90},
	[2004] = {difficulty = 2, groupId = 4, taskId = 20004, monsterId = 312004, description = {{detail = '殷北提到與他碰面的是個女子，但其並不像主腦，因為每次會面時似乎遠處還有人在暗處注視。', brief = '“仇鬼”與一名女子有關'}, {detail = '邢元良交代有人以給他大量的黃金為前提，讓他在虎峽谷製造混亂，但並未說明目的。', brief = '有人希望製造混亂'}, {detail = '從候俊才身上搜到的字條“只要你幫我在村裡散播謠言，就少不了你的好處”。', brief = '有人希望散佈謠言'}, {detail = '據向永長所說，“仇鬼”希望借助幻影門的力量來剿滅虎峽聯，並在成功后將功勞全算在自己頭上。', brief = '“仇鬼”對虎峽聯充滿仇恨'}, {detail = '原來古馨正是“仇鬼”的妹妹，同樣經歷了慘痛過去的她，在暗地裡協助自己的哥哥。', brief = '“仇鬼”是古馨的哥哥'}, }, ringleaderDesc = '心靈扭曲的復仇者。\n在很小的時候，父母便在虎峽聯的一次行動中意外喪命。\n從此他便只有一個目的：復仇。他希望在村裡散播謠言並製造混亂借此擾亂虎峽聯，並收買虎峽聯成員以便獲取內部消息，最后再聯手幻影門覆滅虎峽聯。', story = '虎穴聯成員近日經常在外出調查村民死亡事件時被人暗殺，而村民們之間流傳著一個關于復仇的故事，更暗地裡稱復仇者為“仇鬼”。這些言語傳到虎穴聯，更是鬧得虎穴聯內部人心惶惶。', angleAlive = 90, angleDead = 90},
	[3001] = {difficulty = 3, groupId = 1, taskId = 30001, monsterId = 313001, description = {{detail = '張祿交代說他在一次行竊途中，看到窗內大盜的剪影后，嚇得翻牆而逃了。只隱約聽到一句“放心，那傢伙很有錢，是個財主”', brief = '大盜的目標是財主'}, {detail = '賈懷丹曾見過村裡的賣餅師父和其徒弟幾番出沒于采心大盜的作案現場。但並有看到過程。', brief = '大盜可能與賣餅人有關'}, {detail = '真俄仁曾接待過江湖術士，並且跟他是老相識。江湖術士要求他看好掌櫃的行蹤，但並沒有說明目的。', brief = '江湖術士可能知道大盜真容'}, {detail = '他與錢大掌櫃找來的打手之一非常交好，與那神秘江湖術士似乎也多有聯繫，這三人曾一起見過面。並講出大盜正是江湖術士找來的。', brief = '大盜與江湖術士合謀'}, {detail = '雖然老頭極力否認自己與大盜的關係，還是從他的身上搜到了一封密信，上面寫著“月掛中天，武院再見”八個字', brief = '大盜仍在南林湖'}, }, ringleaderDesc = '賣餅人穀亦糧曾經的師弟，真正的采心大盜。\n受雇于江湖術士之托，在南林湖採集12份心頭血。江湖術士與錢兩貫合作后，由真俄仁潛入錢家為金拙尋找機會，除掉錢一枚。妄圖一石二鳥，既能集齊心頭血，又可輔助錢兩貫上位，得到兩份傭金。', story = '南林湖最近發生了11起凶案，令村民們人心惶惶，更令人恐懼的是每具屍首都被挖去了心臟，人們都稱兇手為采心大盜，但卻沒人知道此人到底是誰，也不清楚此人的目的！', angleAlive = 90, angleDead = 90},
