i3k_db_power_reputation =
{
	[1] = { name = '花間平訴', desc = '花亭平原民團組織，由頌武門所培訓的民兵構成，一直致力于處理在花亭平原發生的大小事情。這些事情大到有人入侵花亭平原，小到百姓家吵架的雞毛蒜皮等等。\n現任首領花飛英一直想將自己的勢力擴大到花亭平原外面，不懈的努力著。', icon = 6517, openLevel = 79, againstID = 0, npcTeleport = 11023, npcLeader = 11022, npcSupport = 11023, npcCommitTrans = 11027, npcTaskTrans = 11024, commitDesc = '將東西交給我們，妳一定不會后悔。', itemID = 67241, desc2 = '與其他勢力關系一般'},
	[2] = { name = '松山府', desc = '松月關當地旨在維護安穩的武林人士所組成的兵團，與管理松月關的松月城主為合作關系。主要對抗四處做亂的神地兵士，一直在打壓城外的伏波寨，想要將伏波寨剿滅。\n現任府主行動雷厲風行，行事果斷。\n與<c=red>伏波水莊</c>為敵對勢力，獲得聲望時<c=red>減少敵對聲望</c>', icon = 6518, openLevel = 89, againstID = 3, npcTeleport = 11124, npcLeader = 11123, npcSupport = 11124, npcCommitTrans = 11128, npcTaskTrans = 11125, commitDesc = '松山府會讓這些東西都用在刀刃上！', itemID = 67242, desc2 = '與伏波水莊為敵對勢力，獲得聲望時減少敵對聲望'},
	[3] = { name = '伏波水莊', desc = '伏波寨的分部，期初是專門為了處理松月關與百姓有關的事情而設立。后期拓展事務，逐漸擴展到了負責周邊的安全，同時也防止被松山府剿滅。同松山府勢成水火，誰也不讓。\n伏波水莊秉持著劫富濟貧的理念，保護百姓持續到今天。\n與<c=red>松山府</c>為敵對勢力，獲得聲望時<c=red>減少敵對聲望</c>', icon = 6519, openLevel = 89, againstID = 2, npcTeleport = 11130, npcLeader = 11129, npcSupport = 11130, npcCommitTrans = 11134, npcTaskTrans = 11131, commitDesc = '伏波水莊一直致力于幫助百姓，所以妳們可以放心將東西交給我們。', itemID = 67243, desc2 = '與松山府為敵對勢力，獲得聲望時減少敵對聲望'},
	[4] = { name = '虎闊軍', desc = '虎穴聯為了讓虎峽谷保持平穩而組織的軍隊，一旦虎峽谷有事發生就會立刻出動。整體風格有如疾風暴雨般的雷厲風行，對敵人絕對不手軟。\n目前對虎峽谷內的幻影門毫不姑息，就算是已經脫離了幻影門的影亂山也是他們剿滅的目標。\n與<c=red>影亂山</c>為敵對勢力，獲得聲望時<c=red>減少敵對聲望</c>', icon = 6520, openLevel = 97, againstID = 5, npcTeleport = 11202, npcLeader = 11201, npcSupport = 11202, npcCommitTrans = 11206, npcTaskTrans = 11203, commitDesc = '有了妳們這些物資，我們虎闊軍就可以更好的幫助大家。', itemID = 67244, desc2 = '與影亂山為敵對勢力，獲得聲望時減少敵對聲望'},
	[5] = { name = '影亂山', desc = '一部分幻影門弟子因不滿幻影門的做法而脫離了幻影門，在影亂山落腳。\n為了成為邪派最大的組織而承接各種各樣的生意，時刻防備著幻影門跟虎闊軍，與這兩個地方經常明裏暗裏的爭鬥不休。\n與<c=red>虎闊軍</c>為敵對勢力，獲得聲望時<c=red>減少敵對聲望</c>', icon = 6521, openLevel = 97, againstID = 4, npcTeleport = 11208, npcLeader = 11207, npcSupport = 11208, npcCommitTrans = 11212, npcTaskTrans = 11209, commitDesc = '嘿嘿嘿，妳們選擇支持影亂山，最后必定是可以得到好處的。', itemID = 67245, desc2 = '與虎闊軍為敵對勢力，獲得聲望時減少敵對聲望'},
	[6] = { name = '嬌蠻千金芙蓉', desc = '北海冰宮譚雨軒之女，脾氣嬌蠻任性，到處惹是生非，讓譚雨軒頗為頭疼。\n從爸爸身邊逃離之后，芙蓉感覺到了自由，但也感覺到在江湖的不易。不過她轉瞬之間就把煩惱扔在一邊，盡情享受沒有人管束的日子。\n人長得漂亮，脾氣雖然有些暴躁，但會適時的表現出小鳥依人的樣子，所以深受江湖中年輕俠士的喜歡，追求者不少。', icon = 8074, openLevel = 95, againstID = 0, npcTeleport = 11234, npcLeader = 11135, npcSupport = 11234, npcCommitTrans = 11138, npcTaskTrans = 11235, commitDesc = '我家芙蓉就是這么可愛，得到大家的喜歡，有機會請她給妳們唱歌！', itemID = 67721, desc2 = '與其他勢力關系一般，互不得罪'},
	[7] = { name = '赤鴉之鬼', desc = '傳說來自遙遠的彼方之島——夜蘿洲的神秘組織，頭領自稱‘魔王’。\n整個組織由羽鴉魔大王所統領，不知什麼原因，藏匿于神武門中不輕易離開。\n隨著夜蘿洲的出現，他們的來歷似乎也明瞭起來。羽鴉魔似乎是在‘三魔王動亂’的鬥爭中敗下陣來的輸家，在中原地區蟄伏，期望能夠重回夜蘿洲。\n該組織不介入任何武林爭端，屬于中立勢力。', icon = 9628, openLevel = 35, againstID = 0, npcTeleport = 10519, npcLeader = 10518, npcSupport = 10519, npcCommitTrans = 0, npcTaskTrans = 10520, commitDesc = '赤鴉之鬼感謝您的慷慨！', itemID = 69001, desc2 = '與其他勢力關係一般，互不得罪'},
}

