i3k_db_collection =
{
	[9901] = { id = 9901, name = '碧玉竹笛', iconID = 1730, rank = 2, desc = '碧玉竹笛乃是由百年前笛王左三星精選泫勃竹林百年玉竹製成的極品橫笛，此笛可演奏千種旋律，物感天地漸漸有了超凡脫俗的靈性。', attrId1 = 1001, attrValue1 = 300, attrId2 = -1, attrValue2 = -1, needId1 = 65729, needValue1 = 10, needId2 = 65835, needValue2 = 5, bindDiamond = 200, needMountCount = 6, needEdgeId1 = 65835, needEdgeValue1 = 5, needEdgeId2 = 67712, needEdgeValue2 = 5, bindEdgeDiamond = 240 },
	[9902] = { id = 9902, name = '沉香沁水環', iconID = 1749, rank = 2, desc = '沉香沁水環乃是用百年沉香木雕琢而成的絕世珍寶，佩戴此環可以提神醒腦，修煉內功時更可以預防走火入魔的情況發生。', attrId1 = 1001, attrValue1 = 300, attrId2 = -1, attrValue2 = -1, needId1 = 65729, needValue1 = 10, needId2 = 65835, needValue2 = 5, bindDiamond = 200, needMountCount = 6, needEdgeId1 = 65835, needEdgeValue1 = 5, needEdgeId2 = 67712, needEdgeValue2 = 5, bindEdgeDiamond = 240 },
	[9903] = { id = 9903, name = '山河社稷圖', iconID = 1739, rank = 3, desc = '山河社稷圖乃是著名畫家丹秋生封筆所作的名畫，此畫中描繪了山河壯闊的景色，作者自己看了也很激動，于是又畫了十年才退休。', attrId1 = 1001, attrValue1 = 450, attrId2 = 1007, attrValue2 = 20, needId1 = 65730, needValue1 = 10, needId2 = 65835, needValue2 = 12, bindDiamond = 500, needMountCount = 9, needEdgeId1 = 65835, needEdgeValue1 = 5, needEdgeId2 = 67712, needEdgeValue2 = 12, bindEdgeDiamond = 600 },
	[9904] = { id = 9904, name = '追雲靴', iconID = 1759, rank = 3, desc = '追雲靴是由百年前世間最頂尖的十名頂級裁縫縫製的巔峰作品。此寶能令穿戴者身法提升三成以上（取決于個人資質）。', attrId1 = 1001, attrValue1 = 450, attrId2 = -1, attrValue2 = -1, needId1 = 65730, needValue1 = 10, needId2 = 65835, needValue2 = 12, bindDiamond = 500, needMountCount = 9, needEdgeId1 = 65835, needEdgeValue1 = 5, needEdgeId2 = 67712, needEdgeValue2 = 12, bindEdgeDiamond = 600 },
	[9905] = { id = 9905, name = '麒麟臂', iconID = 1737, rank = 2, desc = '麒麟臂是始終保持處男之身的麒麟門門主傳承之物，此寶能夠讓佩戴著擁有神秘莫測的麒麟之力，使用者需要經常加餐，注意營養。', attrId1 = 1002, attrValue1 = 60, attrId2 = 1004, attrValue2 = 20, needId1 = 65729, needValue1 = 10, needId2 = 65835, needValue2 = 5, bindDiamond = 200, needMountCount = 6, needEdgeId1 = 65835, needEdgeValue1 = 5, needEdgeId2 = 67712, needEdgeValue2 = 5, bindEdgeDiamond = 240 },
	[9906] = { id = 9906, name = '君子劍', iconID = 1753, rank = 3, desc = '君子劍是百年前劍聖的配劍，劍聖儒劍雙修，以劍入道。此劍能夠令佩戴者氣質文雅，內心平靜，極適合各種易怒人士佩戴。', attrId1 = 1002, attrValue1 = 90, attrId2 = 1005, attrValue2 = 10, needId1 = 65730, needValue1 = 10, needId2 = 65835, needValue2 = 12, bindDiamond = 500, needMountCount = 9, needEdgeId1 = 65835, needEdgeValue1 = 5, needEdgeId2 = 67712, needEdgeValue2 = 12, bindEdgeDiamond = 600 },
	[9907] = { id = 9907, name = '琉璃虎', iconID = 1733, rank = 2, desc = '用仙界琉璃打造的小型雕塑，傳聞此寶封印了一百只老虎（公）的精血，經常與此寶對視可增加武者的膽魄，缺點是在特殊季節佩戴容易吸引母老虎。', attrId1 = 1001, attrValue1 = 300, attrId2 = -1, attrValue2 = -1, needId1 = 65729, needValue1 = 10, needId2 = 65835, needValue2 = 5, bindDiamond = 200, needMountCount = 6, needEdgeId1 = 65835, needEdgeValue1 = 5, needEdgeId2 = 67712, needEdgeValue2 = 5, bindEdgeDiamond = 240 },
	[9908] = { id = 9908, name = '神威機關甲', iconID = 1740, rank = 3, desc = '神威機關甲乃是神工的師父製成的機關盔甲，此甲可謂是機關術之大成，攻守皆屬寶物，更可讓矮小者看起來更偉岸挺拔，增強自信。', attrId1 = 1003, attrValue1 = 120, attrId2 = 1007, attrValue2 = 25, needId1 = 65730, needValue1 = 10, needId2 = 65835, needValue2 = 12, bindDiamond = 500, needMountCount = 9, needEdgeId1 = 65835, needEdgeValue1 = 5, needEdgeId2 = 67712, needEdgeValue2 = 12, bindEdgeDiamond = 600 },
	[9909] = { id = 9909, name = '翻天踏浪靴', iconID = 1750, rank = 2, desc = '翻天踏浪靴是百年前一位能工巧匠所製作的奇物，穿戴此靴可以在水面上任意行走（冰面請注意保暖）。', attrId1 = 1001, attrValue1 = 300, attrId2 = -1, attrValue2 = -1, needId1 = 65729, needValue1 = 10, needId2 = 65835, needValue2 = 5, bindDiamond = 200, needMountCount = 6, needEdgeId1 = 65835, needEdgeValue1 = 5, needEdgeId2 = 67712, needEdgeValue2 = 5, bindEdgeDiamond = 240 },
	[9910] = { id = 9910, name = '紫霞秘本', iconID = 1760, rank = 4, desc = '紫霞秘本中記載了紫霞門中歷代高手修煉紫霞神功的心得，可謂是修煉紫霞神功的捷徑，全書以高清圖片為主，有無文化皆可修煉。', attrId1 = 1001, attrValue1 = 600, attrId2 = 1003, attrValue2 = 50, needId1 = 65730, needValue1 = 20, needId2 = 65835, needValue2 = 18, bindDiamond = 800, needMountCount = 12, needEdgeId1 = 65835, needEdgeValue1 = 5, needEdgeId2 = 67712, needEdgeValue2 = 18, bindEdgeDiamond = 960 },
	[9911] = { id = 9911, name = '麒麟秘寶', iconID = 1738, rank = 3, desc = '麒麟秘寶是當年麒麟門的鎮派之寶，可以平日儲存內力，需要的時候隨時取用補充內力不足（副作用尚不明確）。', attrId1 = 1001, attrValue1 = 450, attrId2 = 1007, attrValue2 = 15, needId1 = 65730, needValue1 = 10, needId2 = 65835, needValue2 = 12, bindDiamond = 500, needMountCount = 9, needEdgeId1 = 65835, needEdgeValue1 = 5, needEdgeId2 = 67712, needEdgeValue2 = 12, bindEdgeDiamond = 600 },
	[9912] = { id = 9912, name = '邪帝舍利', iconID = 1757, rank = 3, desc = '邪帝舍利乃是上一代西邪教教主全身功力所化的結晶，傳聞得到它的有緣人可以從中繼承這些功力——需要先找到教主的傳承人徐仲和寇子陵。', attrId1 = 1001, attrValue1 = 450, attrId2 = 1012, attrValue2 = 120, needId1 = 65730, needValue1 = 10, needId2 = 65835, needValue2 = 12, bindDiamond = 500, needMountCount = 9, needEdgeId1 = 65835, needEdgeValue1 = 5, needEdgeId2 = 67712, needEdgeValue2 = 12, bindEdgeDiamond = 600 },
	[9913] = { id = 9913, name = '碧風藥典', iconID = 1748, rank = 2, desc = '碧風藥典中記載了碧風門數百年來收集的各種藥方，對于一名醫者來說，此寶勝過世間一切珍寶，但書中有很多生僻字，需要想辦法解讀。', attrId1 = 1003, attrValue1 = 80, attrId2 = -1, attrValue2 = -1, needId1 = 65729, needValue1 = 10, needId2 = 65835, needValue2 = 5, bindDiamond = 200, needMountCount = 6, needEdgeId1 = 65835, needEdgeValue1 = 5, needEdgeId2 = 67712, needEdgeValue2 = 5, bindEdgeDiamond = 240 },
	[9914] = { id = 9914, name = '炙妖火', iconID = 1746, rank = 4, desc = '炙妖火乃是火山中千萬年間孕育出的一絲靈火，此火是鍛造神兵的催化劑，許多鍛造大師對其垂涎欲滴，不過傳聞說用來燒火做飯效果也不錯。', attrId1 = 1001, attrValue1 = 600, attrId2 = 1017, attrValue2 = 1, needId1 = 65730, needValue1 = 20, needId2 = 65835, needValue2 = 18, bindDiamond = 800, needMountCount = 12, needEdgeId1 = 65835, needEdgeValue1 = 5, needEdgeId2 = 67712, needEdgeValue2 = 18, bindEdgeDiamond = 960 },
	[9915] = { id = 9915, name = '兩儀劍譜', iconID = 1754, rank = 4, desc = '兩儀劍譜是武林中一直流傳著的傳說。這本劍譜中記載的劍法絕妙莫測，就算學不會其中劍法，也可以從中學習到很多實用的戰鬥技巧。', attrId1 = 1002, attrValue1 = 120, attrId2 = 1006, attrValue2 = 50, needId1 = 65730, needValue1 = 20, needId2 = 65835, needValue2 = 18, bindDiamond = 800, needMountCount = 12, needEdgeId1 = 65835, needEdgeValue1 = 5, needEdgeId2 = 67712, needEdgeValue2 = 18, bindEdgeDiamond = 960 },
	[9916] = { id = 9916, name = '天蠶鼓', iconID = 1741, rank = 3, desc = '天蠶鼓據說是歷經上百場戰爭的戰鼓，此鼓所發出的聲音能夠引起人體內的氣血奔流。如若你體內被中下了鼓蠶，那更會痛苦萬分，說出所有真心話。', attrId1 = 1001, attrValue1 = 450, attrId2 = 1007, attrValue2 = 15, needId1 = 65730, needValue1 = 10, needId2 = 65835, needValue2 = 12, bindDiamond = 500, needMountCount = 9, needEdgeId1 = 65835, needEdgeValue1 = 5, needEdgeId2 = 67712, needEdgeValue2 = 12, bindEdgeDiamond = 600 },
	[9917] = { id = 9917, name = '雪飲狂刀', iconID = 1758, rank = 3, desc = '雪飲狂刀傳說乃是百年前刀魔的武器（副手），被此刀所傷之人會被奪取魂魄變成不人不鬼的怪物，也有一定機率獲得永生（珍惜生命，請勿嘗試）。', attrId1 = 1002, attrValue1 = 90, attrId2 = 1006, attrValue2 = 30, needId1 = 65730, needValue1 = 10, needId2 = 65835, needValue2 = 12, bindDiamond = 500, needMountCount = 9, needEdgeId1 = 65835, needEdgeValue1 = 5, needEdgeId2 = 67712, needEdgeValue2 = 12, bindEdgeDiamond = 600 },
	[9918] = { id = 9918, name = '圓月彎刀', iconID = 1745, rank = 4, desc = '圓月彎刀是用天外隕鐵所打造而成神兵，傳聞此刀在月圓之夜使用，能夠幻化多道刀光迷惑對手，讓對手防不勝防，武林傳聞，非月圓之夜無此效果。', attrId1 = 1002, attrValue1 = 120, attrId2 = 1006, attrValue2 = 50, needId1 = 65730, needValue1 = 20, needId2 = 65835, needValue2 = 18, bindDiamond = 800, needMountCount = 12, needEdgeId1 = 65835, needEdgeValue1 = 5, needEdgeId2 = 67712, needEdgeValue2 = 18, bindEdgeDiamond = 960 },
	[9919] = { id = 9919, name = '千年冰核', iconID = 1736, rank = 2, desc = '千年冰核乃是長白山積累了上千年的冰雪所化的極寒之物，冰核上的寒氣可以輕易凍結除了水和活物之外的任何物體。', attrId1 = 1001, attrValue1 = 300, attrId2 = -1, attrValue2 = -1, needId1 = 65729, needValue1 = 10, needId2 = 65835, needValue2 = 5, bindDiamond = 200, needMountCount = 6, needEdgeId1 = 65835, needEdgeValue1 = 5, needEdgeId2 = 67712, needEdgeValue2 = 5, bindEdgeDiamond = 240 },
