Config.ArenaPeakChampionData.data_explain = {
	[1] = {id=1,desc="1.巅峰冠军赛作为<div fontcolor=#d95014>赛区制</div>全服玩法，整个赛季共分为预选赛（6轮）、晋级赛（2轮）和冠军赛（6轮）三个阶段\n2.赛季开启后，<div fontcolor=#d95014>每天仅进行一轮</div>比赛，每轮比赛分竞猜阶段（<div fontcolor=#d95014>06:00~20:30</div>）、对战阶段（<div fontcolor=#d95014>20:30~20:40</div>）和休整阶段（<div fontcolor=#d95014>20:40~06:00</div>）\n3.赛季于<div fontcolor=#d95014>周日早上6点</div>开启，整个赛季共<div fontcolor=#d95014>持续两周</div>，每间隔四周开启新赛季\n4.届时<div fontcolor=#d95014>跨服竞技场</div>上赛季前<div fontcolor=#d95014>100</div>名玩家<div fontcolor=#d95014>自动</div>进入预选赛\n5.全服所有参与预选赛的选手，按一定人数标准划分出不同赛区，每个赛区至少需要<div fontcolor=#d95014>64</div>名玩家\n6.若全服参赛人员不足<div fontcolor=#d95014>64</div>人，则本赛季无法开启\n7.每轮比赛为<div fontcolor=#d95014>三局两胜</div>制，第一局由双方<div fontcolor=#d95014>第一队伍</div>进行较量，第二局则是<div fontcolor=#d95014>第二队伍</div>之间进行较量，先胜两局者本轮获胜\n8.若本局双方队伍为空，则3支队伍总战力较高者取得本局胜利\n9.玩家每次调整阵容，会在下次分配对手时生效",title="基础规则"},
	[2] = {id=2,desc="1.在<div fontcolor=#d95014>赛季开启时</div>即进入预选赛阶段，共6轮比赛，赛制为积分循环制\n2.每轮比赛选手胜利将获得积分，失败不得分\n3.最后的总积分等于预选赛每轮积分和加上由跨服竞技场排名决定的初始积分，排名越高，初始积分越高\n4.预选赛总分排名前<div fontcolor=#d95014>256</div>名自动进入晋级赛阶段",title="预选赛规则"},
	[3] = {id=3,desc="1.在<div fontcolor=#d95014>预选赛过后</div>即进入晋级赛阶段，共2轮比赛，赛制为淘汰制\n2.分十六组进行，每组决出4名胜者进入冠军赛64强",title="晋级赛规则"},
	[4] = {id=4,desc="1.在<div fontcolor=#d95014>晋级赛过后</div>即进入冠军赛阶段，共6轮比赛，赛制为淘汰制\n2.分八组进行，每组的唯一胜者晋级8强（前3轮）\n3.由晋级的8强决出最终巅峰赛的总冠军（后3轮）",title="冠军赛规则"},
	[5] = {id=5,desc="1.每轮比赛竞猜获得的竞猜币，将在战斗阶段结束后（每晚 20:40）结算为巅峰币，通过邮件发放\n2.巅峰币可用于商店兑换稀有道具",title="奖励发放"},
}
